  while(f) {
    f = false;
    REP(i, 0, h) REP(j, 0, w) {
      if(s[i][j] == '.' || visited[S[i][j]]) {
        int cnt[256];
        REP(k, 0, 256) cnt[k] = 0;
        // cout << i << ", " << j << endl;
