#include <cmath>
#include <complex>
#include <cstdlib>
#include <iostream>
#include <queue>
#include <utility>
#include <vector>
using namespace std;

typedef complex<int> point;
#define X real()
#define Y imag()

constexpr int MAX_SIZE = 500;
string board[MAX_SIZE];

bool operator<(const point& a, const point& b) {
	return a.X != b.X ? a.X < b.X : a.Y < b.Y;
}

inline int dist2(const point& a, const point& b) {
	const int diffX = a.X - b.X;
	const int diffY = a.Y - b.Y;
	return diffX * diffX + diffY * diffY;
}

inline double dist(const point& a, const point& b) {
	return sqrt(dist2(a, b));
}

int calc(const point& start, const point& to) {
	const point d = (to - start) / static_cast<int>(dist(to, start));
	int res = 0;
	point current = start;

	while(current != to) {
		if(board[current.Y][current.X] != '.') res |= (1 << (board[current.Y][current.X] - 'A'));
		current += d;
	}

	return res;
}

void update(queue<int>& que, vector<pair<int, int>>& can_erase, int value) {
	value = ~value;

	for(int i = 0; i < static_cast<int>(can_erase.size());) {
		can_erase[i].first &= value;
		if(can_erase[i].first == 0) {
			que.push(can_erase[i].second);
			can_erase.erase(can_erase.begin() + i);
		}
		else {
			++i;
		}
	}
}

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);

	int h, w;
	cin >> h >> w;

	for(int i = 0; i < h; ++i) {
		cin >> board[i];
	}

	vector<vector<point>> tiles(26);
	for(int i = 0; i < h; ++i) {
		for(int j = 0; j < w; ++j) {
			if(board[i][j] != '.') {
				tiles[board[i][j] - 'A'].emplace_back(j, i);
			}
		}
	}

	vector<bool> used(26, true);
	vector<pair<int, int>> can_erase;

	for(int i = 0; i < 26; ++i) {
		if(tiles[i].size() == 0 || dist2(tiles[i][0], tiles[i][1]) == 1) continue;

		used[i] = false;
		if(tiles[i][1] < tiles[i][0]) swap(tiles[i][0], tiles[i][1]);

		if(tiles[i][0].X == tiles[i][1].X) {
			can_erase.emplace_back(calc(tiles[i][0] + point(0, 1), tiles[i][1]), i);
		}
		else if(tiles[i][0].Y == tiles[i][1].Y) {
			can_erase.emplace_back(calc(tiles[i][0] + point(1, 0), tiles[i][1]), i);
		}
		else {
			point start(tiles[i][0].X, tiles[i][1].Y);
			can_erase.emplace_back(calc(start, tiles[i][0]) | calc(start, tiles[i][1]), i);

			start = point(tiles[i][1].X, tiles[i][0].Y);
			can_erase.emplace_back(calc(start, tiles[i][0]) | calc(start, tiles[i][1]), i);
		}
	}

	queue<int> que;
	update(que, can_erase, 0);

	int ans = 0;
	while(!que.empty()) {
		int color = que.front();
		que.pop();

		if(used[color]) continue;
		used[color] = true;
		++ans;

		update(que, can_erase, (1 << color));
	}

	cout << ans * 2 << endl;

	return EXIT_SUCCESS;
}

