typedef vector<int> VI;
typedef vector<VI> VVI;
typedef vector<string> VS;
typedef pair<int, int> PII;
typedef long long LL;

//container util
//------------------------------------------
#define ALL(a)  (a).begin(),(a).end()
#define RALL(a) (a).rbegin(), (a).rend()
#define PB push_back
#define MP make_pair
#define SZ(a) int((a).size())
#define EACH(i,c) for(typeof((c).begin()) i=(c).begin(); i!=(c).end(); ++i)
#define EXIST(s,e) ((s).find(e)!=(s).end())
#define SORT(c) sort((c).begin(),(c).end())

//repetition
//------------------------------------------
#define FOR(i,a,b) for(int i=(a);i<(b);++i)
#define REP(i,n)  FOR(i,0,n)

//constant
//--------------------------------------------
const double EPS = 1e-10;
const double PI  = acos(-1.0);

int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  int H, W; cin >> H >> W;
  VS vs(H);
  REP(i,H) cin >> vs[i];

  vector<PII> memo(32, MP(-1,-1));
  vector<pair<PII,PII>> pos;
  REP(y,H) REP(x,W){
	if(vs[y][x] != '.'){
	  int i = vs[y][x] - 'A';
	  if(memo[i].first < 0)
		memo[i] = MP(x,y);
	  else
		pos.PB(MP(memo[i], MP(x,y)));
	}
  }

  int ans = 0;
  bool up  = true;
  while(up){
	up = false;

	for(int i=0;i<SZ(pos);++i){
	  PII p1 = pos[i].first, p2 = pos[i].second;
	  if(p1 > p2) swap(p1,p2);
	  if((p1.first+1 == p2.first && p1.second == p2.second)
		 || (p1.first == p2.first && abs(p1.second-p2.second)==1)) continue;

	  bool ok = true;
	  for(int j=0;j<SZ(pos);++j){
		if(i == j) continue;
		PII q1 = pos[j].first, q2 = pos[j].second;
		if(((q1.second == p1.second || q1.second == p2.second) && p1.first <= q1.first && q1.first <= p2.first)
		   && ((q2.second == p1.second || q2.second == p2.second) && p1.first <= q2.first && q2.first <= p2.first)){
		  ok = false;
		  break;
		}
	  }
	  if(ok){
		pos.erase(pos.begin() + i);
		ans += 2;
		up = true;
		break;
	  }
	}
  }

  cout << ans << endl;

  return 0;
}

#include <iostream>
#include <vector>
#include <algorithm>
#define REP(i, a, n) for(int i = ((int) a); i < ((int) n); i++)
using namespace std;

int M, N, K;
char S[550][550];
int h, w;
vector<int> y, x;
vector<char> c;
char s[100][100];
int dd[5] = { 0, 1, 0, -1, 0 };

int compress(vector<int> &a) {
  vector<int> v;

  REP(i, 0, a.size()) v.push_back(a[i]);
  sort(v.begin(), v.end());
  v.erase(unique(v.begin(), v.end()), v.end());
  REP(i, 0, a.size()) a[i] = find(v.begin(), v.end(), a[i]) - v.begin();

  return (int) v.size();
}

bool isin(int y, int x) {
  return 0 <= y && y < h && 0 <= x && x < w;
}

int main(void) {
  cin >> M >> N;
  K = 0;
  REP(i, 0, M) REP(j, 0, N) {
    cin >> S[i][j];
    if(S[i][j] != '.') {
      y.push_back(i);
      x.push_back(j);
      c.push_back(S[i][j]);
      K++;
    }
  }

  h = compress(y);
  w = compress(x);

  REP(i, 0, h) REP(j, 0, w) s[i][j] = '.';
  REP(i, 0, K) {
    // cout << y[i] << ", " << x[i] << ": " << c[i] << endl;
    s[y[i]][x[i]] = c[i];
  }

  // REP(i, 0, h) {
  //   REP(j, 0, w) cout << s[i][j];
  //   cout << endl;
  // }

  int ans = 0;
  bool visited[256];
  REP(i, 0, 256) visited[i] = false;
  bool f = true;
  while(f) {
    f = false;
    REP(i, 0, h) REP(j, 0, w) {
      if(s[i][j] == '.' || visited[s[i][j]]) {
        int cnt[256];
        REP(k, 0, 256) cnt[k] = 0;
        // cout << i << ", " << j << endl;
        REP(k, 0, 4) {
          int ny = i + dd[k], nx = j + dd[k + 1];
          while(isin(ny, nx)) {
            // cout << "  " << ny << ", " << nx << ": " << s[ny][nx] << endl;
            if(s[ny][nx] != '.' && !visited[s[ny][nx]]) {
              // cout << s[ny][nx] << endl;
              cnt[s[ny][nx]]++;
              if(cnt[s[ny][nx]] == 2) {
                // cout << "  vanish: " << S[ny][nx] << endl;
                visited[s[ny][nx]] = true;
                ans += 2;
                f = true;
              }
              break;
            }
            ny += dd[k];
            nx += dd[k + 1];
          }
        }
      }
    }
  }
  cout << ans << endl;

  return 0;
}

