#define REP(i,n) for(int i=0;i<(int)(n);i++)

using namespace std;

int main() {
  int m,n;
  cin>>m>>n;
  vector<string> t(m);
  REP(i,m)cin>>t[i];
  vector<int> iv,jv;
  REP(i,m) {
    bool al = true;
    REP(j,n) if(t[i][j] != '.') al = false;
    if(!al){
      if(i) iv.push_back(i-1);
      iv.push_back(i);
      if(i<m-1) iv.push_back(i+1);
    }
  }
  iv.erase(unique(begin(iv),end(iv)),end(iv));
  REP(i,n) {
    bool al = true;
    REP(j,m) if(t[j][m] != '.') al = false;
    if(!al){
      if(i) jv.push_back(i-1);
      jv.push_back(i);
      if(i<n-1) jv.push_back(i+1);
    }
  }
  jv.erase(unique(begin(jv),end(jv)),end(jv));
  if (iv.empty()) {
    cout << 0 << endl;
    return 0;
  }
  vector<string> ct(iv.size());
  m = iv.size();
  n = jv.size();
  REP(i,m)
    REP(j,n)
      ct[i] += t[iv[i]][jv[j]];
  int score = 0;
  while(1){
    set<char> er;
    REP(i,m)REP(j,n){
      if (ct[i][j] != '.') continue;
      map<char, int> s;
      int di[]={1,0,-1,0};
      int dj[]={0,1,0,-1};
      REP(k,4){
        int l=1;
        while(1){
          int ni=i+di[k]*l;
          int nj=j+dj[k]*l;
          if(ni<0||nj<0||ni>=m||nj>=n) break;
          if(ct[ni][nj] != '.'){
            ++s[ct[ni][nj]];
            break;
          }
          ++l;
        }
      }
      for (auto p:s) {
        if(p.second == 2) er.insert(p.first);
      }
    }
    score += er.size()*2;
    if (er.empty()) break;
    REP(i,m)REP(j,n){
      if(er.count(ct[i][j]))
        ct[i][j] = '.';
    }
  }
  cout << score << endl;
  return 0;
}

