int main(){
	int h,w;
	cin >> h >> w;
	vector<vector<char> > map(h, vector<char>(w));
	vector<vector<pair<int,int> > > place(26);
	vector<int> exist(26, false);
	for(int i=0; i<h; i++){
		for(int j=0; j<w; j++){
			cin >> map[i][j];
			if(map[i][j]!='.'){
				place[map[i][j]-'A'].push_back(make_pair(i,j));
				exist[map[i][j]-'A'] = true;
			}
		}
	}

	bool nochange = false;
	int score = 0;
	while(!nochange){
		nochange = true;
		for(int i=0; i<='Z'-'A'; i++){
			if(!exist[i]) continue;
			int x1 = place[i][0].first, y1 = place[i][0].second;
			int x2 = place[i][1].first, y2 = place[i][1].second;
			bool success = true;
			bool tap = false;
			if(x1 == x2){
				for(int j=min(y1,y2)+1; j<max(y1,y2); j++){
					if(map[x1][j]=='.'){
						tap=true;
					}else{
						success = false;
						break;
					}
				}
			}else if(y1 == y2){
				for(int j=min(x1,x2)+1; j<max(x1,x2); j++){
					if(map[j][y1]=='.'){
						tap=true;
					}else{
						success = false;
						break;
					}
				}
			}else{
				tap = true;
				for(int j=min(y1,y2)+1; j<max(y1,y2); j++){
					if(map[x1][j]!='.'){
						success = false;
						break;
					}
				}
				for(int j=min(x1,x2)+1; j<max(x1,x2); j++){
					if(map[j][y2]!='.'){
						success = false;
						break;
					}
				}
				if(map[x1][y2]!='.'){
					success = false;
				}
				if(!success){
					success = true;
					for(int j=min(y1,y2)+1; j<max(y1,y2); j++){
						if(map[x2][j]!='.'){
							success = false;
							break;
						}
					}
					for(int j=min(x1,x2)+1; j<max(x1,x2); j++){
						if(map[j][y1]!='.'){
							success = false;
							break;
						}
					}
					if(map[x2][y1]!='.'){
						success = false;
					}
				}
			}

			if(success && tap){
				score += 2;
				map[x1][y1] = map[x2][y2] = '.';
				exist[i] = false;
				nochange = false;
			}
		}
	}
	cout << score << endl;
    return 0;
}

