int evaluate(int x, int y, int width, int height, const vector<string> &field){
	int score = 0;
	int count[26] = { 0 };
	if(field[y][x] != '.'){ return 0; }
	for(int i = x - 1; i >= 0; --i){
		if(field[y][i] != '.'){
			++count[field[y][i] - 'A'];
			break;
		}
	}
	for(int i = x + 1; i < width; ++i){
		if(field[y][i] != '.'){
			++count[field[y][i] - 'A'];
			break;
		}
	}
	for(int i = y - 1; i >= 0; --i){
		if(field[i][x] != '.'){
			++count[field[i][x] - 'A'];
			break;
		}
	}
	for(int i = y + 1; i < height; ++i){
		if(field[i][x] != '.'){
			++count[field[i][x] - 'A'];
			break;
		}
	}
	for(int i = 0; i < 26; ++i){
		if(count[i] == 2){ score += 2; }
	}
	return score;
}

