	while(true){
		bool modified = false;
		pii hit;
		for(int i = 0; i < 26 && !modified; ++i){
			if(pos[i].empty()){ continue; }
			pii p0 = pos[i][0], p1 = pos[i][1];
			if(p0.first == p1.first){
				int minx = min(p0.second, p1.second), maxx = max(p0.second, p1.second);
				for(int x = minx + 1; x < maxx; ++x){
					if(evaluate(x, p0.first, n, m, field) > 0){
						modified = true;
						hit = pii(p0.first, x);
						break;
					}
				}
			}else if(p0.second == p1.second){
				int miny = min(p0.first, p1.first), maxy = max(p0.first, p1.first);
				for(int y = miny + 1; y < maxy; ++y){
					if(evaluate(p0.second, y, n, m, field) > 0){
						modified = true;
						hit = pii(y, p0.second);
						break;
					}
				}
			}else{
				if(evaluate(p0.second, p1.first, n, m, field) > 0){
					modified = true;
					hit = pii(p1.first, p0.second);
				}
				if(evaluate(p1.second, p0.first, n, m, field) > 0){
					modified = true;
					hit = pii(p0.first, p1.second);
				}
			}
		}
		if(!modified){ break; }
		int count[26] = { 0 };
		for(int i = hit.second - 1; i >= 0; --i){
			if(field[hit.first][i] != '.'){
				++count[field[hit.first][i] - 'A'];
				break;
			}
		}
		for(int i = hit.second + 1; i < n; ++i){
			if(field[hit.first][i] != '.'){
				++count[field[hit.first][i] - 'A'];
				break;
			}
		}
		for(int i = hit.first - 1; i >= 0; --i){
			if(field[i][hit.second] != '.'){
				++count[field[i][hit.second] - 'A'];
				break;
			}
		}
		for(int i = hit.first + 1; i < m; ++i){
			if(field[i][hit.second] != '.'){
				++count[field[i][hit.second] - 'A'];
				break;
			}
		}
		for(int i = 0; i < m; ++i){
			for(int j = 0; j < n; ++j){
				if(field[i][j] == '.'){ continue; }
				if(count[field[i][j] - 'A'] >= 2){
					pos[field[i][j] - 'A'].clear();
					field[i][j] = '.';
					++answer;
				}
			}
		}
	}
	cout << answer << endl;
	return 0;
}

#include "bits/stdc++.h"
//http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=2299&lang=jp
using namespace std;

int dx[4] = { 1,0,-1,0 };
int dy[4] = { 0,-1,0,1 };

vector<int> search(int m, int n, vector<vector<char>>& C,int key) {
	vector<int> dir;
	vector<vector<int>> alp(26);
	for (int i = 0; i < 4;i++) {
		for (int k = 1;k < key;k++) {
			int im = m + k*dx[i];
			int in = n + k*dy[i];
			if (C[im][in] == '#')break;
			if (C[im][in] != '.') {
				alp[C[im][in] - 'A'].push_back(i);
				break;
			}
		}
	}
	for (int i = 0; i < 26;i++) {
		if (alp[i].size() < 2)continue;
		for (int j = 0; j < (int)alp[i].size();j++) {
			dir.push_back(alp[i][j]);
		}
	}
	return dir;
}

void del(int m, int n, vector<vector<char>>& C, int key, int dir) {
	for (int k = 1;k < key;k++) {
		int im = m + k*dx[dir];
		int in = n + k*dy[dir];
		if (C[im][in] == '#')break;
		if (C[im][in] != '.') {
			C[im][in] = '.';
			break;
		}
	}
}

int main() {
	cin.tie(0); ios::sync_with_stdio(false);
	int M, N; cin >> M >> N;
	vector<vector<char>> C(M+2, vector<char>(N+2, '#'));
	for (int i = 1; i <= M;i++) for (int j = 1; j <= N;j++) cin >> C[i][j];

	int ans = 0;
	int key = max(N, M);
	while (true) {
		bool f = false;
		for (int i = 1;i <= M;i++) {
			for (int j = 1;j <= N;j++) {
				if (C[i][j] == '.') {
					auto Dir = search(i, j, C, key);
					for (int k = 0; k < (int)Dir.size();k++) {
						if(!f)f = true;
						ans++;
						del(i, j, C, key, Dir[k]);
					}
				}
			}
		}
		if (!f)break;
	}
	cout << ans << endl;
}

#include <iostream>
#include <vector>
#include <string>
