  for(int i=0;i<30;i++)if(a[i].size()) saiki(i);

  for(int i=0;i<m;i++)
    for(int j=0;j<n;j++) if(mp[i][j]!='.') ans--;

  cout<<ans<<endl;
  return 0;
}

#include<iostream>
