	{
		P up    = make_pair(Que.front().first - 1, Que.front().second);
		P down  = make_pair(Que.front().first + 1, Que.front().second);
		P left  = make_pair(Que.front().first, Que.front().second - 1);
		P right = make_pair(Que.front().first, Que.front().second + 1);
