using namespace std;

/* constant */

const int MAX_M = 500;
const int MAX_N = 500;

typedef long long ll;

const int INF = 1 << 30;
const ll LINF = 1LL << 60;

/* typedef */

typedef vector<int> vi;
typedef queue<int> qi;
typedef pair<int,int> pii;

/* global variables */

int flds[MAX_M][MAX_N];
pii ps[26], as[26];
vi msks[26];
bool used[26];

/* subroutines */

string b2s(int bits) {
  string s;
  for (int i = 0, bi = 1; i < 26; i++, bi <<= 1)
    if (bits & bi) s += (char)('A' + i);
  return (s.empty() ? "." : s);
}

/* main */

int main() {
  int m, n;
  cin >> m >> n;

  for (int i = 0; i < 26; i++) ps[i].first = -1;

  for (int y = 0; y < m; y++) {
    string s;
    cin >> s;
    for (int x = 0; x < n; x++)
      flds[y][x] = (s[x] == '.') ? -1 : s[x] - 'A';
  }

  for (int y = 0; y < m; y++)
    for (int x = 0; x < n; x++) {
      int k = flds[y][x];
      if (k < 0) continue;

      if (ps[k].first < 0) {
	ps[k] = pii(x, y);
	continue;
      }

      int x0 = ps[k].first, y0 = ps[k].second;
      if (x != x0 && y != y0) {
	int msk0 = 0, msk1 = 0;
	int dx = (x > x0) ? 1 : -1;

	for (int x1 = x; x1 != x0; x1 -= dx)
	  if (flds[y0][x1] >= 0) msk0 |= (1 << flds[y0][x1]);
	for (int y1 = y0; y1 != y; y1++)
	  if (flds[y1][x] >= 0) msk0 |= (1 << flds[y1][x]);

	for (int x1 = x0; x1 != x; x1 += dx)
	  if (flds[y][x1] >= 0) msk1 |= (1 << flds[y][x1]);
	for (int y1 = y; y1 != y0; y1--)
	  if (flds[y1][x0] >= 0) msk1 |= (1 << flds[y1][x0]);

	if (msk0 == msk1)
	  msks[k].push_back(msk0);
	else {
	  msks[k].push_back(msk0);
	  msks[k].push_back(msk1);
	}
      }
      else if (x == x0) {
	if (y0 + 1 < y) {
	  int msk0 = 0;
	  for (int y1 = y0 + 1; y1 < y; y1++)
	    if (flds[y1][x] >= 0) msk0 |= (1 << flds[y1][x]);
	  msks[k].push_back(msk0);
	}
      }
      else {
	if (x0 + 1 < x) {
	  int msk0 = 0;
	  for (int x1 = x0 + 1; x1 < x; x1++)
	    if (flds[y][x1] >= 0) msk0 |= (1 << flds[y][x1]);
	  msks[k].push_back(msk0);
	}
      }
    }

  if (false) {
    for (int i = 0; i < 26; i++)
      if (msks[i].size()) {
	printf("%c:(%lu)", 'A' + i, msks[i].size());
	for (int j = 0; j < msks[i].size(); j++)
	  cout << ' ' << b2s(msks[i][j]);
	putchar('\n');
      }
  }

  int msk = 0;
  int ans = 0;

  for (bool changed = true; changed;) {
    changed = false;
    for (int i = 0, bi = 1; i < 26; i++, bi <<= 1)
      if (! used[i] && msks[i].size()) {
	for (int j = 0; j < msks[i].size(); j++)
	  if ((msk & msks[i][j]) == msks[i][j]) {
	    used[i] = true;
	    msk |= bi;
	    ans++;
	    changed = true;
	    break;
	  }
      }
  }

  printf("%d\n", ans * 2);
  return 0;
}

