const int MAX = 2000010;
long long fac[MAX], finv[MAX], inv[MAX];

// 
void COMinit() {
    fac[0] = fac[1] = 1;
    finv[0] = finv[1] = 1;
    inv[1] = 1;
    for (int i = 2; i < MAX; i++){
        fac[i] = fac[i - 1] * i % MOD;
        inv[i] = MOD - inv[MOD%i] * (MOD / i) % MOD;
        finv[i] = finv[i - 1] * inv[i] % MOD;
    }
}

// 
long long COM(int n, int k){
    if (n < k) return 0;
    if (n < 0 || k < 0) return 0;
    return fac[n] * (finv[k] * finv[n - k] % MOD) % MOD;
}

long long extGCD(long long a, long long b, long long &x, long long &y) {
    if (b == 0) {
        x = 1;
        y = 0;
        return a;
    }
    long long d = extGCD(b, a%b, y, x);
    y -= a/b * x;
    return d;
}
//  mod (a = -11  OK) 
inline long long mod(long long a, long long m) {
    return (a % m + m) % m;
}

//  ( a  m )
long long modinv(long long a, long long m) {
    long long x, y;
    extGCD(a, m, x, y);
    return mod(x, m); //  x % m x 
}
ll GCD(ll a, ll b){

    if(b == 0) return a;
    return GCD(b, a%b);
}

typedef vector<ll> vec;
typedef vector<vec> mat;

mat mul(mat &A, mat &B) {
    mat C(A.size(), vec((int)B[0].size()));
    for(int i=0; i<A.size(); ++i){
        for(int k=0; k<B.size(); ++k){
            for(int j=0; j<B[0].size(); ++j){
                C[i][j] = (C[i][j] + A[i][k] * B[k][j] %MOD) % MOD;
            }
        }
    }
    return C;
}
mat matPow(mat A, ll n) {
    mat B(A.size(), vec((int)A.size()));

    for(int i=0; i<A.size(); ++i){
        B[i][i] = 1;
    }

    while(n > 0) {
        if(n & 1) B = mul(B, A);
        A = mul(A, A);
        n >>= 1;
    }
    return B;
}

map<ll,ll> prime_factor(ll n) {
  map<ll,ll> res;
  for(ll i=2; i*i <= n; i++) {
      while(n%i == 0) {
          res[i]++;
          n /= i;
      }
  }

  if(n != 1) res[n] = 1;
  return res;
}

struct Node {
    int mi, mx;
    Node(int mi, int mx) : mi(mi), mx(mx) {}
    Node(){}
} seg[1<<23];

void update(int i, int x){
    i += (1<<22)-1;
    seg[i].mi = seg[i].mx = x;

    while(i > 0){
        i = (i-1)/2;
        seg[i].mi = min(seg[i*2+1].mi, seg[i*2+2].mi);
        seg[i].mx = max(seg[i*2+1].mx, seg[i*2+2].mx);
    }
}

Node query(int a, int b, int k=0, int l=0, int r = (1<<22)){
    if(r <= a || b <= l) return Node(INT_MAX, -INT_MAX);
    if(a <= l && r <= b) return seg[k];
    else{
        Node vl = query(a, b, k*2+1, l, (l+r)/2);
        Node vr = query(a, b, k*2+2, (l+r)/2, r);

        return Node(min(vl.mi, vr.mi), max(vl.mx, vr.mx));
    }
}

