bool check(vector<string> &s, int sx,int sy){
    map<int,int> mp;
    int gx,gy;
    bool f=false;
    for(int i=0;i<4;i++){
        gx=sx,gy=sy;
        while(true){
            gx += dx[i]; gy += dy[i];
            if(gx<0 || m<=gx || gy<0 || n<=gy) break;
            if(s[gx][gy]=='.') continue;
            int idx=s[gx][gy]-'A';
