                bad[y][x] = true;
                continue;
            }
            for(char c='A';c<='Z';c++){
                auto& ps = s[c];
                if(ps.size()>=2){
                    for(auto & p : ps){
                        int x,y;
                        tie(x,y) = p;
                        g[y][x] = '.';
                        ans++;
                        finish = false;
                        space.emplace_back(x,y);
                    }
                }
            }
        }
        if(finish) break;
    }
    return ans;
}

