    int nx=c-'A';
    if(nx==x) continue;
    if(c!='.'&&!erased[nx]&&!func(nx)) fb=0;
  }
  for(int i=x1;i<=x2;i++){
    char c=s[y2][i];
    int nx=c-'A';
    if(nx==x) continue;
    if(c!='.'&&!erased[nx]&&!func(nx)) fc=0;
  }
  for(int i=y1;i<=y2;i++){
    char c=s[i][x2];
    int nx=c-'A';
    if(nx==x) continue;
    if(c!='.'&&!erased[nx]&&!func(nx)) fd=0;
  }
  if(!f) f=(fa&fd)|(fb&fc);
  else f=(fa&fb)|(fc&fd);
  if((y1==y2&&x1+1==x2)||(x1==x2&&y1+1==y2)) f=0;
  if(!f) return false;
  erased[x]=true;
  return true;
}

