struct Point {
  int x;
  int y;
  Point(int _x,int _y) : x(_x) , y(_y) {}
};

int main(){
  int H,W;
  char stage[500][500];

  map<char,vector<Point> > tiles;
  while(~scanf("%d %d",&H,&W)){
    for(int y=0;y<H;y++){
      string line;
      cin >> line;
      for(int x=0;x<W;x++){
	stage[y][x] = line[x];
	if(stage[y][x] != '.'){
	  tiles[stage[y][x]].push_back(Point(x,y));
	}
      }
    }

    int erased_count = 0;
    bool erased[256];
    memset(erased,false,sizeof(erased));
    erased['.'] = true;

    for(int round=0;round <= 500*500; round++){
      for(int target_char = 'A'; target_char <= 'Z'; target_char++){
	if(erased[target_char]) continue;
	if(tiles.find(target_char) == tiles.end()) continue;

	int x1 = tiles[target_char][0].x;
	int y1 = tiles[target_char][0].y;

	int x2 = tiles[target_char][1].x;
	int y2 = tiles[target_char][1].y;

	bool isok = true;
	if(x1 == x2){
	  if(abs(y1-y2) == 1) isok = false;

	  int x = x1;
	  for(int y=min(y1,y2)+1;y<max(y1,y2);y++){
	    if(!erased[stage[y][x]]){
	      isok = false;
	      break;
	    }
	  }
	}
	else if(y1==y2){
	  if(abs(x1-x2) == 1) isok = false;

	  int y = y1;
	  for(int x=min(x1,x2)+1;x<max(x1,x2);x++){
	    if(!erased[stage[y][x]]){
	      isok = false;
	      break;
	    }
	  }
	}
	else{

	  vector<Point> medium;
	  int wrong_count = 0;
	  for(int i=0;i<tiles[target_char].size();i++){
	    for(int j=0;j<tiles[target_char].size();j++){
	      if(!((x1 == tiles[target_char][i].x && y1 == tiles[target_char][j].y)
		   || (x2 == tiles[target_char][i].x && y2 == tiles[target_char][j].y))){
		Point mid(tiles[target_char][i].x,tiles[target_char][j].y);

		//target x1,y1
		int flag1 = 0;
		for(int x = min(mid.x,x1);x<max(mid.x,x1); x++){
		  if(!erased[stage[y1][x]]) flag1 |= 1;
		}

		for(int y = min(mid.y,y1);y<max(mid.y,y1); y++){
		  if(!erased[stage[y][x1]]) flag1 |= (1<<1);
		}

		//target x2,y2
		int flag2 = 0;
		for(int x = min(mid.x,x2);x<max(mid.x,x2); x++){
		  if(!erased[stage[y2][x]]) flag2 |= 1;
		}

		for(int y = min(mid.y,y2);y<max(mid.y,y2); y++){
		  if(!erased[stage[y][x2]]) flag2 |= (1<<1);
		}

		if(flag1 == ((1<<2)-1) && flag2 == ((1<<2)-1)){
		  wrong_count++;
		}
	      }
	    }

	    if(wrong_count == 2){
	      isok = false;
	    }
	  }
	}

	if(isok){
	  erased[target_char] = true;
	  erased_count++;
	}
      }
    }

    printf("%d\n",erased_count*2);
  }
}

#include<iostream>
using namespace std;

string grid[500];
int posy[26][2],posx[26][2];
int pos[26][2][2];
bool use[26];
int n,m;
int res;

bool cluck(int y,int x){
  int k;
  int cnt[26];
  for(int i=0;i<26;i++)cnt[i] = 0;

  k = 1;
  while(y+k<n && grid[y+k][x] == '.')k++;
  if(y+k<n)cnt[grid[y+k][x]-'A']++;

  k = 1;
  while(x+k<m && grid[y][x+k] == '.')k++;
  if(x+k<m)cnt[grid[y][x+k]-'A']++;

  k = 1;
  while(y-k>=0 && grid[y-k][x] == '.')k++;
  if(y-k>=0)cnt[grid[y-k][x]-'A']++;

  k = 1;
  while(x-k>=0 && grid[y][x-k] == '.')k++;
  if(x-k>=0)cnt[grid[y][x-k]-'A']++;

  bool f = false;
  for(int i=0;i<26;i++){
    if(cnt[i]>=2){
      res += 2;
      f = true;
      for(int j=0;j<n;j++){
	for(int k=0;k<m;k++){
	  if(grid[j][k] == 'A'+i)grid[j][k] ='.';
	}
      }
    }
  }
  return f;
}

int main(){
  cin >> n >> m;
  for(int i=0;i<n;i++)cin >> grid[i];

  for(int i=0;i<26;i++){
    posy[i][0] = -1;
    posx[i][0] = -1;
    posy[i][1] = -1;
    posx[i][1] = -1;
  }

  for(int i=0;i<n;i++){
    for(int j=0;j<m;j++){
      if(grid[i][j] != '.'){
	if(posy[grid[i][j]-'A'][0]<0){
	  posy[grid[i][j]-'A'][0] = i;
	  posx[grid[i][j]-'A'][0] = j;
	}else{
	  posy[grid[i][j]-'A'][1] = i;
	  posx[grid[i][j]-'A'][1] = j;
	}
      }
    }
  }

  res = 0;
  while(1){
    bool f = false;
    for(int i=0;i<26;i++){
      if(posy[i][0] == posy[i][1]){
	int s = min(posx[i][0],posx[i][1]),g = max(posx[i][0],posx[i][1]);
	for(int j=s+1;j<g;j++){
	  if(grid[posy[i][0]][j]=='.')f |= cluck(posy[i][0],j);
	}
      }else if(posx[i][0] == posx[i][1]){
	int s = min(posy[i][0],posy[i][1]),g = max(posy[i][0],posy[i][1]);
	for(int j=s+1;j<g;j++){
	  if(grid[j][posx[i][0]]=='.')f |= cluck(j,posx[i][0]);
	}
      }else{
	if(grid[posy[i][0]][posx[i][1]] == '.')f |= cluck(posy[i][0],posx[i][1]);
	if(grid[posy[i][1]][posx[i][0]] == '.')f |= cluck(posy[i][1],posx[i][0]);
      }
    }
    if(!f)break;
  }

  cout << res << endl;
}

#include <bits/stdc++.h>

using namespace std;

#define MAX_H 500
#define MAX_W 500
#define F first
#define S second
typedef pair<int,int> pii;

int H,W;
char field[MAX_H][MAX_W];
vector<pii> v[26];

bool inField(int x,int y){
    return (0 <= x && x < W && 0 <= y && y < H);
}

int calc(int x,int y){
    for(int i = 0 ; i < 26 ; i++){
        v[i].clear();
    }
    const int dx[] = {-1,0,1,0};
    const int dy[] = {0,-1,0,1};
    for(int i = 0 ; i < 4 ; i++){
        int nx = x, ny = y;
        while(1){
            nx += dx[i]; ny += dy[i];
            if(!inField(nx,ny)) break;
            if(field[ny][nx] != '.'){
                v[field[ny][nx]-'A'].push_back(pii(nx,ny));
                break;
            }
        }
    }
    int res = 0;
    for(int i = 0 ; i < 26 ; i++){
        if(v[i].size() == 2){
            field[v[i][0].S][v[i][0].F] = '.';
            field[v[i][1].S][v[i][1].F] = '.';
            res += 2;
        }
    }
    return res;
}

int main(){
    cin >> H >> W;
    for(int i = 0 ; i < H ; i++){
        for(int j = 0 ; j < W ; j++){
            cin >> field[i][j];
        }
    }
    int res = 0;
    bool update = 1;
    while(update){
        update = 0;
        for(int i = 0 ; i < H ; i++){
            for(int j = 0 ; j < W ; j++){
                if(field[i][j] != '.') continue;
                int r = calc(j,i);
                if(r > 0){
                    res += r;
                    update = 1;
                }
            }
        }
    }
    cout << res << endl;
    return 0;
}

#include<iostream>
#include<map>
#include<vector>
#include<algorithm>

using namespace std;

int H,W;
typedef pair<int,int>P;

int dx[] = {0,1,0,-1};
int dy[] = {1,0,-1,0};

const int TYPEX = 1;
const int TYPEY = 2;
const int ELSE = 3;

class Data{
public:
  vector<P>pv;
  bool used;
  int type;
  Data(){pv.clear();used = true;type = 0;};
  void check(){
    sort(pv.begin(),pv.end());
    for(int i = 0 ; i < 4 ; i++){
      P np = P(pv[0].first + dy[i],pv[0].second + dx[i]);
      if(!(0 <= np.first && np.first < H && 0 <= np.second && np.second < W))continue;
      if(pv[1] == np){
	used = true;
	return;
      }
    }
    if(pv[0].first == pv[1].first)type = TYPEY;
    else if(pv[0].second == pv[1].second)type = TYPEX;
    else type = ELSE;
  }
};

Data data[26];

void init(){
  for(int i = 0 ; i < 26 ; i++)data[i] = Data();
}

void input(){
  cin >> H >> W;
  for(int i = 0 ; i < H ; i++){
    for(int j = 0 ; j < W ; j++){
      char c;
      cin >> c;
      if(c == '.')continue;
      data[c-'A'].pv.push_back(P(i,j));
      data[c-'A'].used = false;
    }
  }
  for(int i = 0 ; i < 26 ; i++){
    if(!data[i].used)data[i].check();
  }
  /*
  for(int i = 0 ; i < 26 ; i++){
    cout << "i = " << i << " used = " << data[i].used << endl;
  }
  */
}

int run(int res){
  for(int i = 0 ; i < 26 ; i++){
    if(data[i].used)continue;
    bool f = false;
    P p0,p1,p2;
    p0 = data[i].pv[0];
    p1 = data[i].pv[1];

    if(data[i].type == TYPEX){
      for(int j = 0 ; j < 26 ; j++){
	if(f)break;
	if(j == i)continue;
	if(data[j].used)continue;
	for(int k = 0 ; k < 2 ; k++){
	  p2 = data[j].pv[k];
	  if(p0.second != p2.second)continue;
	  if(p0.first < p2.first && p2.first < p1.first){
	    f = true;
	    break;
	  }
	}
      }
    }
    else if(data[i].type == TYPEY){
      for(int j = 0 ; j < 26 ; j++){
	if(f)break;
	if(j == i)continue;
	if(data[j].used)continue;
	for(int k = 0 ; k < 2 ; k++){
	  p2 = data[j].pv[k];
	  if(p0.first != p2.first)continue;
	  if(p0.second < p2.second && p2.second < p1.second){
	    f = true;
	    break;
	  }
	}
      }
    }
    else {
      for(int j = 0 ; j < 26 ; j++){
	if(f)break;
	if(j == i)continue;
	bool f1,f2;
	f1 = f2 = false;
	if(data[j].used)continue;

	for(int k = 0 ; k < 2 ; k++){
	  p2 = data[j].pv[k];
	  int x1,x2,y1,y2;
	  if(p0.first < p1.first){
	    x1 = p0.second;
	    y1 = p0.first;
	    x2 = p1.second;
	    y2 = p1.first;

	    if(p2.second == x1){
	      if(y1 <= p2.first && p2.first <= y2)f1 = true;
	    }

	    if(p2.first == y1){
	      if(x1 <= p2.second && p2.second <= x2)f2 = true;
	    }

	    if(p2.second == x2){
	      if(y1 <= p2.first && p2.first <= y2)f2 = true;
	    }

	    if(p2.first == y2){
	      if(x1 <= p2.second && p2.second <= x2)f1 = true;
	    }

	  }
	  else{
	    x1 = p0.second;
	    y1 = p1.first;
	    x2 = p1.second;
	    y2 = p0.first;

	    if(p2.second == x1){
	      if(y1 <= p2.first && p2.first <= y2)f1 = true;
	    }

	    if(p2.first == y1){
	      if(x1 <= p2.second && p2.second <= x2)f1 = true;
	    }

	    if(p2.second == x2){
	      if(y1 <= p2.first && p2.first <= y2)f2 = true;
	    }

	    if(p2.first == y2){
	      if(x1 <= p2.second && p2.second <= x2)f2 = true;
	    }
	  }
	}
	if(f1 && f2){
	  f = true;
	  break;
	}
      }
    }
    if(f)continue;
    //cout << "del = " << (char)(i + 'A') << endl;//
    data[i].used = true;
    return run(res+2);
  }
  return res;
}

int main(){
  init();
  input();
  cout << run(0) << endl;
  return 0;
}

#include <iostream>
#include <vector>
#include <tuple>
#include <algorithm>
using namespace std;
using T=tuple<int,int,char>;
int main(){
    int M,N;
    cin>>M>>N;
    vector<T> V;
    for(int i=0;i<M;i++){
        for(int j=0;j<N;j++){
            char c;
            cin>>c;
            if(c!='.'){
                V.emplace_back(j,i,c);
            }
        }
    }
    vector<int> X,Y;
    for(auto t:V){
        int x,y;
        tie(x,y,ignore)=t;
        X.emplace_back(x);
        Y.emplace_back(y);
        sort(X.begin(), X.end());
        sort(Y.begin(), Y.end());
    }
    X.erase(unique(X.begin(), X.end()),X.end());
    vector<int> XX;
    XX.push_back(X[0]);
    for(int i=1;i<X.size();i++){
        if(X[i-1]+1!=X[i]){
            XX.push_back(X[i-1]+1);
        }
        XX.push_back(X[i]);
    }
    swap(X,XX);
    Y.erase(unique(Y.begin(), Y.end()),Y.end());
    vector<int> YY;
    YY.push_back(Y[0]);
    for(int i=1;i<Y.size();i++){
        if(Y[i-1]+1!=Y[i]){
            YY.push_back(Y[i-1]+1);
        }
        YY.push_back(Y[i]);
    }
    swap(Y,YY);
    int x_max=X.size()-1;
    int y_max=Y.size()-1;
    char board[Y.size()][X.size()];//y,x
    for(int i=0;i<=y_max;i++){
        for(int j=0;j<=x_max;j++){
            board[i][j]='.';
        }
    }
    for(auto &t:V){
        int x,y; char c;
        tie(x,y,c)=t;
        x=lower_bound(X.begin(), X.end(),x)-X.begin();
        y=lower_bound(Y.begin(), Y.end(),y)-Y.begin();
        board[y][x]=c;
    }
    /*
    for(int i=0;i<=y_max;i++){
        for(int j=0;j<=x_max;j++){
            cout<<board[i][j];
        }
        cout<<endl;
    }*/
    bool updated=true;
    int ans=0;
    while(updated){
        updated=false;
        for(int y=0;y<=y_max;y++){
            for(int x=0;x<=x_max;x++){
                if(board[y][x]!='.') continue;
                char up='u',down='d',left='l',right='r';
                int upd,downd,leftd,rightd;
                for(int d=1;x+d<=x_max;d++){
                    if(board[y][x+d]!='.'){
                        right=board[y][x+d];
                        rightd=d;
                        break;
                    }
                }
                for(int d=-1;0<=x+d;d--){
                    if(board[y][x+d]!='.'){
                        left=board[y][x+d];
                        leftd=d;
                        break;
                    }
                }
                for(int d=1;y+d<=y_max;d++){
                    if(board[y+d][x]!='.'){
                        down=board[y+d][x];
                        downd=d;
                        break;
                    }
                }
                for(int d=-1;0<=y+d;d--){
                    if(board[y+d][x]!='.'){
                        up=board[y+d][x];
                        upd=d;
                        break;
                    }
                }
                if(up==down){
                    board[y+upd][x]=board[y+downd][x]='.';
                    ans+=2;
                    updated=true;
                }
                if(up==left){
                    board[y+upd][x]=board[y][x+leftd]='.';
                    ans+=2;
                    updated=true;
                }
                if(up==right){
                    board[y+upd][x]=board[y][x+rightd]='.';
                    ans+=2;
                    updated=true;
                }
                if(down==left){
                    board[y+downd][x]=board[y][x+leftd]='.';
                    ans+=2;
                    updated=true;
                }
                if(down==right){
                    board[y+downd][x]=board[y][x+rightd]='.';
                    ans+=2;
                    updated=true;
                }
                if(left==right){
                    board[y][x+leftd]=board[y][x+rightd]='.';
                    ans+=2;
                    updated=true;
                }
            }
        }
    }
    cout<<ans<<endl;

    return 0;
}

