    for(int i=0;i<2;i++){
        for(int j=0;j<4;j++){
            y = pos[i].first;
            x = pos[i].second;
            do{
                y += dy[j];
                x += dx[j];
                if(f[y][x] == '.'){
                    reachable[i][y][x] = true;
