            }while(f[y][x] == '.');
        }
    }
    for(int i=1;i<=m;i++){
        for(int j=1;j<=n;j++){
            if(reachable[0][i][j] && reachable[1][i][j]){
                return true;
            }
        }
    }
    return false;
}

