			int ny2=y,nx2=x;
			while(V[ny2][nx2]=='.'){ny2+=dy[r2];nx2+=dx[r2];if(!in(ny2,nx2)) break;}
			if(!in(ny2,nx2)) continue;

			if(V[ny1][nx1]==V[ny2][nx2]){
				res++;
				V[ny1][nx1]=V[ny2][nx2]='.';
				return true;
			}
		}
	}
