#include <iostream>
#include <vector>
#include <algorithm>
#include <cstdlib>
using namespace std;

char field[505][505];

typedef vector<vector<int> > Graph;

int search(int x, int y, int lx, int ly)
{
	int res = 0;
	for(int i=y; i<=ly; i++) 
	for(int j=x; j<=lx; j++) {
		if(field[i][j] == '.') continue;
		int a = field[i][j] - 'A';
		res |= (1<<a);
	}

	return res;
}

int main()
{
	int M,N;
	while(cin >> N >> M) {

		vector<vector<pair<int, int> > > pos(26);
		for(int i=0; i<N; i++)
			cin >> field[i];

		for(int i=0; i<N; i++) 
		for(int j=0; j<M; j++) {
			if(field[i][j] == '.') continue;
			int a = field[i][j] - 'A';
			pos[a].push_back(make_pair(j,i));
		}

		Graph graph(26);
		for(int i=0; i<26; i++) {
			if(pos[i].size() == 0) continue;

			int x = pos[i][0].first;
			int y = pos[i][0].second;

			int a = pos[i][1].first;
			int b = pos[i][1].second;

			if(x == a) {
				if(y > b) swap(y, b);
				if(b - y == 1) continue;

				int hoge = search(x, y+1, a, b-1);
				graph[i].push_back(hoge);

			}
			else if(y == b) {
				if(x > a) swap(x, a);
				if(a - x == 1) continue;

				int hoge = search(x+1, y, a-1, b);
				graph[i].push_back(hoge);
			}
			else {
				int l = min(x, a);
				int r = min(y, b);

				int c = max(x, a);
				int d = max(y, b);
				// /

				if( l != x) {
					int hoge = 0;
					hoge |= search(l, r, c-1, r);
					hoge |= search(l, r, l, d-1);

					graph[i].push_back(hoge);

					hoge = 0;
					hoge |= search(l+1, d, c, d);
					hoge |= search(c, r+1, c, d);

					graph[i].push_back(hoge);
				}
				else {
					int hoge = 0;
					hoge |= search(l+1, r, c, r);
					hoge |= search(c, r, c, d-1);

					graph[i].push_back(hoge);

					hoge = 0;
					hoge |= search(l, d, c-1, d);
					hoge |= search(l, r+1, l, d);

					graph[i].push_back(hoge);
				}
			}
		}

		int piyo = 0, res = 0;
		for(int i=0; i<26; i++) 
		for(int j=0; j<26; j++) 
		for(int k=0; k<graph[j].size(); k++) {
			if((piyo & graph[j][k]) == graph[j][k]) {
				if((piyo >> j & 1) == 0) {
					piyo |= (1<<j);
					res++;
				}
			}
		}

		cout << res * 2 << endl;
	}
}

#include <iostream>
