using namespace std;

typedef pair<int,int> P;

int m,n;
vector<P> dat[100];
vector<int> xind;
vector<int> yind;
int field[280][280];
int dx[4]={1,0,-1,0};
int dy[4]={0,1,0,-1};
int pickl[4];
int picklx[4];
int pickly[4];

int main(void){
	scanf("%d %d",&n,&m);
	memset(field,0,sizeof(field));
	for(int i=0;i<n;i++){
		string str;
		cin >> str;
		for(int j=0;j<m;j++){
			if(str[j]>='A' && str[j]<='Z'){
				dat[str[j]-'A'+1].push_back(P(j,i));
				if(j-1>=0)xind.push_back(j-1);
				xind.push_back(j);
				if(j+1<m)xind.push_back(j+1);
				if(i-1>=0)yind.push_back(i-1);
				yind.push_back(i);
				if(i+1<n)yind.push_back(i+1);
			}
		}
	}
	sort(xind.begin(),xind.end());
	xind.erase(unique(xind.begin(),xind.end()),xind.end());
	sort(yind.begin(),yind.end());
	yind.erase(unique(yind.begin(),yind.end()),yind.end());
	for(int i=1;i<=26;i++){
		for(int j=0;j<dat[i].size();j++){
			dat[i][j].first=lower_bound(xind.begin(),xind.end(),dat[i][j].first)-xind.begin();
			dat[i][j].second=lower_bound(yind.begin(),yind.end(),dat[i][j].second)-yind.begin();
			field[dat[i][j].first][dat[i][j].second]=i;
		}
	}
	bool flag=true;
	int score2=0;
	while(flag){
		flag=false;
		for(int i=0;i<yind.size();i++){
			for(int j=0;j<xind.size();j++){
				if(field[j][i]==0){
					memset(pickl,0,sizeof(pickl));
					for(int k=0;k<4;k++){
						int xx=j,yy=i;
						int xi=xind.size(),yi=yind.size();
						while(xx>=0 && xx<=xi && yy>=0 && yy<=yi){
							if(field[xx][yy]!=0)break;
							xx+=dx[k];
							yy+=dy[k];
						}
						if(field[xx][yy]!=0){
							pickl[k]=field[xx][yy];
							picklx[k]=xx;
							pickly[k]=yy;
						}
					}
					for(int k=0;k<4;k++){
						for(int l=k+1;l<4;l++){
							if(pickl[k]==pickl[l] && pickl[k]!=0){
								score2+=2;
								field[picklx[k]][pickly[k]]=0;
								field[picklx[l]][pickly[l]]=0;
								flag=true;
							}
						}
					}
				}
			}
		}
	}
	printf("%d\n",score2);
	return 0;
}

#include <iostream>
#include <string>
#include <cstring>
#include <cmath>
using namespace std;

string mps[501];
pair<int,int> tile1[26];
pair<int,int> tile2[26];
int m,n;

bool inter(int i,int j,int k) {
    if(j == k && i != j) return false;
    if(j > k) swap(j,k);

    return (j <= i) && (i <= k);
}

bool ok(int idx,int x1,int y1,int x2,int y2) {
    // adj
    if(x1 == x2 && abs(y1-y2) == 1) return false;
    if(y1 == y2 && abs(x1-x2) == 1) return false;

    for(int i=0; i<26; ++i) {
        if(tile1[i].first == -1 || i == idx) continue;
        if(x1 == x2) {
            if(tile1[i].second == x1 && inter(tile1[i].first,y1,y2)) {
                if(tile2[i].second == x1 && inter(tile2[i].first,y1,y2)) return false;
            }
        }else if(y1 == y2) {
            if(tile1[i].first == y1 && inter(tile1[i].second,x1,x2)) {
                if(tile2[i].first == y1 && inter(tile2[i].second,x1,x2)) return false;
            }
        }
    }

    bool is1 = false,is2 = false;
    for(int i=0; i<26; ++i) {
        if(tile1[i].first == -1 || i == idx) continue;
        if(tile1[i].first == y1 && inter(tile1[i].second,x1,x2)) is1 = true;
        if(tile1[i].first == y2 && inter(tile1[i].second,x1,x2)) is2 = true;
        if(tile2[i].first == y1 && inter(tile2[i].second,x1,x2)) is1 = true;
        if(tile2[i].first == y2 && inter(tile2[i].second,x1,x2)) is2 = true;
    }

    if(is1 && is2) return false;

    return true;
}

int main() {
    cin>>m>>n;
    for(int i=0; i<m; ++i) cin>>mps[i];

    for(int i=0; i<26; ++i)
        tile1[i] = tile2[i] = make_pair(-1,-1);

    for(int i=0; i<m; ++i) {
        for(int j=0; j<n; ++j) {
            if(mps[i][j] != '.') {
                if(tile1[mps[i][j]-'A'].first == -1) tile1[mps[i][j]-'A'] = make_pair(i,j);
                else tile2[mps[i][j]-'A'] = make_pair(i,j);
            }
        }
    }

    int ans = 0;
    while(true) {
        bool update = false;

        for(int i=0; i<26; ++i) {
            if(tile1[i].first == -1) continue;
            int x1 = tile1[i].second,y1 = tile1[i].first;
            int x2 = tile2[i].second,y2 = tile2[i].first;
            if(ok(i,x1,y1,x2,y2)) {
                tile1[i] = make_pair(-1,-1);
                tile2[i] = make_pair(-1,-1);
                ans += 2;
                update = true;
            }

        // for(int i=0; i<m; ++i) {
        //     for(int j=0; j<n; ++j) {
        //         if(mps[i][j] == '.') cout<<".";
        //         else if(tile1[mps[i][j]-'A'].first == -1) cout<<".";
        //         else cout<<mps[i][j];
        //     }
        //     cout<<endl;
        // }
        }

        if(!update) break;


    }

    cout<<ans<<endl;
}

