int main() {
    int N, M;
    cin >> N >> M;
    vector<string> S(N);
    for (auto& s : S) cin >> s;

    int ans = 0;
    bool update = true;
    while (update) {
        update = false;
        for (int cx = 0; cx < N; ++cx) {
            for (int cy = 0; cy < M; ++cy) {
                if (S[cx][cy] != '.') continue;

                string app;
                for (int x = cx; x >= 0; --x) {
                    if (S[x][cy] != '.') {
                        app.push_back(S[x][cy]);
                        break;
                    }
                }
                for (int x = cx; x < N; ++x) {
                    if (S[x][cy] != '.') {
                        app.push_back(S[x][cy]);
                        break;
                    }
                }
                for (int y = cy; y >= 0; --y) {
                    if (S[cx][y] != '.') {
                        app.push_back(S[cx][y]);
                        break;
                    }
                }
                for (int y = cy; y < M; ++y) {
                    if (S[cx][y] != '.') {
                        app.push_back(S[cx][y]);
                        break;
                    }
                }

                sort(app.begin(), app.end());
                if (app.empty()) continue;

                for (int i = 0; i < app.size() - 1; ++i) {
                    if (app[i] == app[i + 1]) {
                        for (int x = 0; x < N; ++x) {
                            replace(S[x].begin(), S[x].end(), app[i], '.');
                        }
                        ans += 2;
                        update = true;
                    }
                }
            }
        }
    }

    cout << ans << endl;
    return 0;
}

