signed main(){
    cin>>H>>W;
    rep(i,H)cin>>fld[i];

    rep(i,H)rep(j,W){
        if(fld[i][j]=='.')continue;
        int t=fld[i][j]-'A';
        if(!ex[t]){
            ex[t]=true;
            x[t]=j;y[t]=i;
        }
        else{
            xx[t]=j;yy[t]=i;
        }
    }

    rep(i,26){
