        if((x[i]==xx[i]&&abs(y[i]-yy[i])==1)||(y[i]==yy[i]&&abs(x[i]-xx[i])==1)){
            vec[i][0].pb(i);vec[i][1].pb(i);
            continue;
        }
        for(int j=min(y[i],yy[i]);j<=max(y[i],yy[i]);j++){
            if(fld[j][x[i]]!='.'&&fld[j][x[i]]!='A'+i)vec[i][0].pb(fld[j][x[i]]-'A');
            if(fld[j][xx[i]]!='.'&&fld[j][xx[i]]!='A'+i)vec[i][1].pb(fld[j][xx[i]]-'A');
        }
        for(int j=min(x[i],xx[i]);j<=max(x[i],xx[i]);j++){
            if(fld[yy[i]][j]!='.'&&fld[yy[i]][j]!='A'+i)vec[i][0].pb(fld[yy[i]][j]-'A');
            if(fld[y[i]][j]!='.'&&fld[y[i]][j]!='A'+i)vec[i][1].pb(fld[y[i]][j]-'A');
        }
    }

    rep(i,26){
        rep(j,2){
            sort(all(vec[i][j]));
            vec[i][j].erase(unique(all(vec[i][j])),vec[i][j].end());
        }
    }
    int ans=0;
    while(true){
        bool update=false;
        rep(i,26){
            if(!ex[i])continue;
            rep(j,2){
                bool ok=true;
                for(auto k:vec[i][j]){
                    if(ex[k])ok=false;
                }
                if(!ok)continue;
                ans++;
                ex[i]=false;
                update=true;
                break;
            }
        }
        if(!update)break;
    }
    cout<<ans*2<<endl;
    return 0;
}

