#include<cstdio>
#include<vector>

#define rep(i,n) for(int i=0;i<(n);i++)

using namespace std;

struct P{ int x,y; };

char g[500][501];

bool checkH(int x1,int x2,int y,char c){
	for(int x=x1;x<=x2;x++) if(g[y][x]!='.' && g[y][x]!=c) return false;
	return true;
}

bool checkV(int y1,int y2,int x,char c){
	for(int y=y1;y<=y2;y++) if(g[y][x]!='.' && g[y][x]!=c) return false;
	return true;
}

int main(){
	int m,n; scanf("%d%d",&m,&n);
	vector<P> pos[26];
	rep(i,m){
		scanf("%s",g[i]);
		rep(j,n) if(g[i][j]!='.') {
			 pos[g[i][j]-'A'].push_back((P){j,i});
		}
	}

	int ans=0;
	while(1){
		bool change=false;

		rep(i,26) if(pos[i].size()==2) {
			P p=pos[i][0],q=pos[i][1];

			bool ok=false;
			if(p.x!=q.x && p.y!=q.y){
				if(checkH(min(p.x,q.x),max(p.x,q.x),p.y,'A'+i)
				&& checkV(min(p.y,q.y),max(p.y,q.y),q.x,'A'+i)
				|| checkH(min(p.x,q.x),max(p.x,q.x),q.y,'A'+i)
				&& checkV(min(p.y,q.y),max(p.y,q.y),p.x,'A'+i)) ok=true;
			}
			else if(p.y==q.y){
				int mn=min(p.x,q.x),mx=max(p.x,q.x);
				if(mx-mn>1 && checkH(mn,mx,p.y,'A'+i)) ok=true;
			}
			else{ // p.x==q.x
				int mn=min(p.y,q.y),mx=max(p.y,q.y);
				if(mx-mn>1 && checkV(mn,mx,p.x,'A'+i)) ok=true;
			}

			if(ok){
				g[p.y][p.x]=g[q.y][q.x]='.';
				pos[i].erase(pos[i].begin(),pos[i].end());
				ans+=2;
				change=true;
			}
		}

		if(!change) break;
	}

	printf("%d\n",ans);

	return 0;
}

