int main() {
    int m, n;
    cin >> m >> n;
    vector<string> map(m);
    for (auto &a : map)cin >> a;
    bool f = 1;
    int score = 0;
    while (f) {
        f = 0;
        rep(i, m)rep(j, n) {
            if (map[i][j] != '.')continue;
            vector<pair<char,string::iterator>> buf;
            rep(k, 4) {
                int x = j, y = i;
                while (x >= 0 && x < n&&y>=0 && y < m&&map[y][x] == '.')x += dx[k], y += dy[k];
                if (x >= 0 && x < n&&y>=0 && y < m)buf.push_back(make_pair(map[y][x],map[y].begin()+x));
            }
            sort(ALL(buf), [](pair<char, string::iterator> a, pair<char, string::iterator> b) {return a.first < b.first; });
            if (buf.size() < 2)continue;
            rep1(i, buf.size()-1) {
                if (buf[i].first == buf[i - 1].first) {
                    *(buf[i].second) = *(buf[i - 1].second) = '.';
                    score += 2;
                    f = 1;
                }
            }
        }
    }
    cout << score << endl;
}

