#include <iostream>
#include <vector>
#define X second
#define Y first
#define A 26
#define N 500
using namespace std;
typedef pair<int,int> P;
vector<P> v[A];
string s[N];
int m,n,ans;
bool used[A],erased[A];

bool func(int x){
  if(!v[x].size()) return false;
  if(used[x]) return false;
  used[x]=true;
  int y1=v[x][0].Y,x1=v[x][0].X,y2=v[x][1].Y,x2=v[x][1].X;
  int f=0,fa=1,fb=1,fc=1,fd=1;
  if(x1>x2) swap(x1,x2),f=1;
  for(int i=x1;i<=x2;i++){
    char c=s[y1][i];
    int nx=c-'A';
    if(nx==x) continue;
    if(c!='.'&&!erased[nx]&&!func(nx)) fa=0;
  }
  for(int i=y1;i<=y2;i++){
    char c=s[i][x1];
    int nx=c-'A';
    if(nx==x) continue;
    if(c!='.'&&!erased[nx]&&!func(nx)) fb=0;
  }
  for(int i=x1;i<=x2;i++){
    char c=s[y2][i];
    int nx=c-'A';
    if(nx==x) continue;
    if(c!='.'&&!erased[nx]&&!func(nx)) fc=0;
  }
  for(int i=y1;i<=y2;i++){
    char c=s[i][x2];
    int nx=c-'A';
    if(nx==x) continue;
    if(c!='.'&&!erased[nx]&&!func(nx)) fd=0;
  }
  if(!f) f=(fa&fd)|(fb&fc);
  else f=(fa&fb)|(fc&fd);
  if((y1==y2&&x1+1==x2)||(x1==x2&&y1+1==y2)) f=0;
  if(!f) return false;
  erased[x]=true;
  return true;
}

int main(){
  cin>>m>>n;
  for(int i=0;i<m;i++) cin>>s[i];
  for(int i=0;i<m;i++)
    for(int j=0;j<n;j++)
      if(s[i][j]!='.') v[s[i][j]-'A'].push_back(P(i,j));
  for(int i=0;i<A;i++){
    for(int j=0;j<A;j++) used[j]=false;
    func(i);
  }
  for(int i=0;i<A;i++) ans+=erased[i]*2;
  cout<<ans<<endl;
  return 0;
}

