using namespace std;

const int N = 500;

typedef pair<int, int> P;
typedef pair<P, P> Point;

int h, w;
char data[N][N];
Point pos[26];
bool used[26];
int dy[] = {-1, 0, 1, 0};
int dx[] = {0, 1, 0, -1};

bool check(P p){
  if(p.first < 0 || p.first >= h) return false;
  if(p.second < 0 || p.second >= w) return false;
  return true;
}

bool check(char c, P s, P t, int d){
  P n = s;
  n.first += dy[d];
  n.second += dx[d];
  bool f = false;
  while(check(n)){
    if(data[n.first][n.second] == '.'){
      f = true;
    }else if(data[n.first][n.second] != c){
      f = false;
      break;
    }
    if(n == t) break;
    n.first += dy[d];
    n.second += dx[d];
  }
  return f;
}

bool erase(char c){
  P a = pos[c-'A'].first;
  P b = pos[c-'A'].second;
  P s = a, t = b;
  int d;
  bool f = false;
  if(a.first == b.first){
    if(a.second < b.second) d = 1;
    else d = 3;
    if(check(c, s, t, d)) f = true;
  }else if(a.second == b.second){
    if(a.first < b.first) d = 2;
    else d = 0;
    if(check(c, s, t, d)) f = true;
  }else{
    if(data[a.first][b.second] == '.'){
      s = a;
      t = P(a.first, b.second);
      if(a.second < b.second) d = 1;
      else d = 3;
      bool f1 = check(c, s, t, d);
      s = b;
      if(a.first < b.first) d = 0;
      else d = 2;
      bool f2 = check(c, s, t, d);
      if(f1 && f2) f = true;
    }
    if(data[b.first][a.second] == '.'){
      s = b;
      t = P(b.first, a.second);
      if(a.second < b.second) d = 3;
      else d = 1;
      bool f1 = check(c, s, t, d);
      s = a;
      if(a.first < b.first) d = 2;
      else d = 0;
      bool f2 = check(c, s, t, d);
      if(f1 && f2) f = true;
    }
  }
  if(f){
    used[c-'A'] = false;
    data[a.first][a.second] = '.';
    data[b.first][b.second] = '.';
  }
  return f;
}

main(){
  cin >> h >> w;
  for(int i=0;i<h;++i){
    for(int j=0;j<w;++j){
      cin >> data[i][j];
      if(data[i][j] != '.'){
        if(used[data[i][j]-'A']){
          pos[data[i][j]-'A'].second = P(i, j);
        }else{
          pos[data[i][j]-'A'].first = P(i, j);
          used[data[i][j]-'A'] = true;
        }
      }
    }
  }
  bool f = true;
  int ans = 0;
  while(f){
    f = false;
    for(int i=0;i<26;i++){
      if(used[i]){
        if(erase(i+'A')){
          f = true;
          ans += 2;
        }
      }
    }
  }
  cout << ans << endl;
  return 0;
}

