typedef vector<string> vs;
int h,w;
vs g;
inline bool valid(int x, int y){
    return 0<=x && 0<=y && x<w && y<h;
}

int solve(){
    vector<pii> space;
    rep(i,h)rep(j,w)if(g[i][j]=='.') space.emplace_back(j,i);
    int ans = 0;
    while(1){
        bool finish = true;
        for(auto p : space){
            vector<pii> s['Z'+1];
            int x,y;
            tie(x,y)=p;
            int dx[]={1,0,-1,0};
            int dy[]={0,1,0,-1};
            rep(d,4){
                int cx,cy;
                tie(cx,cy)=tie(x,y);
                while(valid(cx+dx[d],cy+dy[d]) && g[cy][cx]=='.'){
                    cx+=dx[d]; cy+=dy[d];
                }
                if(valid(cx,cy) && g[cy][cx]!='.'){
                    s[g[cy][cx]].emplace_back(cx,cy);
                }
            }
            for(char c='A';c<='Z';c++){
                auto& ps = s[c];
                if(ps.size()>=2){
                    for(auto & p : ps){
                        int x,y;
                        tie(x,y) = p;
                        g[y][x] = '.';
                        ans++;
                        finish = false;
                        space.emplace_back(x,y);
                    }
                }
            }
        }
        if(finish) break;
    }
    return ans;
}

int main(){
    while(cin>>h>>w){
        g.clear();
        rep(i,h){
            string s;cin>>s;
            g.push_back(s);
        }
        cout << solve() << endl;
    }
}

