const int INF = 1 << 28;

int N, M;
char mas[500][500];
int countx[500], county[500];


bool counter(int x, int y){
  vector< Pi > points;
  if(countx[x] != 0){
    for(int i = y - 1; i >= 0; i--){
      if(mas[i][x] != '.'){
        points.push_back( Pi( x, i));
        break;
      }
    }
    for(int i = y + 1; i < M; i++){
      if(mas[i][x] != '.'){
        points.push_back( Pi( x, i));
        break;
      }
    }
  }
  if(county[y] != 0){
    for(int i = x - 1; i >= 0; i--){
      if(mas[y][i] != '.'){
        points.push_back( Pi( i, y));
        break;
      }
    }

    for(int i = x + 1; i < N; i++){
      if(mas[y][i] != '.'){
        points.push_back( Pi( i, y));
        break;
      }
    }
  }

  for(int i = 0; i < points.size(); i++){
    for(int j = i + 1; j < points.size(); j++){
      Pi& p1 = points[i], p2 = points[j];
      if(mas[p1.second][p1.first] == mas[p2.second][p2.first]){
        mas[p1.second][p1.first] = mas[p2.second][p2.first] = '.';
        county[p1.second]--, county[p2.second]--;
        countx[p1.first]--, countx[p2.first]--;
        return(true);
      }
    }
  }
  return(false);
}

int solve(){
  for(int i = 0; i < M; i++){
    for(int j = 0; j < N; j++){
      if(county[i] + countx[j] < 2) continue;
      if(mas[i][j] == '.' && counter( j, i)) return(2 + solve());
    }
  }
  return(0);
}

int main(){

  cin >> M >> N;
  for(int i = 0; i < M; i++){
    for(int j = 0; j < N; j++){
      cin >> mas[i][j];
    }
  }
  for(int i = 0; i < M; i++){
    for(int j = 0; j < N; j++){
      county[i] += mas[i][j] != '.';
      countx[j] += mas[i][j] != '.';
    }
  }
  cout << solve() << endl;
}

