using namespace std;

char b[512][512];
int w, h;
char memo[4][500][500];

#define LEFT (0)
#define RIGHT (1)
#define DOWN (2)
#define UP (3)

int dx[] = {-1, 1, 0, 0};
int dy[] = {0, 0, 1, -1};


char iter(int y, int x, int d){
  if(y >= h) return '.';
  if(x >= w) return '.';
  if(y <  0) return '.';
  if(x <  0) return '.';

  if(memo[d][y][x]) return memo[d][y][x];
  if(b[y][x] != '.') return b[y][x];

  return memo[d][y][x] = iter(y + dy[d], x + dx[d], d);
}
void update(int y, int x, int d){
  if(memo[d][y][x] == 0 && b[y][x] == '.')
    memo[d][y][x] = iter(y + dy[d], x + dx[d], d);
}

void debug(){
  REP(i,h) puts(b[i]); puts("");
}

int main(){
  scanf("%d%d", &h, &w);

  REP(i, h) scanf("%s", b[i]);

  REP(d, 4) REP(i, h) REP(j, w) if(b[i][j] == '.'){
    if(memo[d][i][j] == 0){
      update(i, j, d);
    }
  }

  int ans = 0;
  bool ok = true;

  while(ok){
    ok = false;

    REP(i,h) REP(j,w) if(b[i][j] == '.'){
      set<int> deld;

      REP(d, 4){
	for(int e = d + 1; e < 4; e++){
	  if(memo[d][i][j] != '.' && memo[d][i][j] == memo[e][i][j]){
	    deld.insert(e);
	    deld.insert(d);
	  }
	}
      }

      if(deld.size()){
	//printf("%d %d : %c %c %c %c\n", i, j, memo[0][i][j], memo[1][i][j], memo[2][i][j], memo[3][i][j]);
	ok = true;
	FOR(it, deld){
	  int y = i;
	  int x = j;
	  int d = *it;

	  while(true){
	    if(b[y][x] != '.'){
	      b[y][x] = '.';
	      break;
	    }
	    y += dy[d];
	    x += dx[d];
	    if(y >= h) break;
	    if(x >= w) break;
	    if(y <  0) break;
	    if(x <  0) break;
	  }
	}

	REP(d,4){
	  REP(y,h) REP(x,w)
	    memo[d][y][x] = 0;

	  REP(y,h) REP(x,w)
	    update(y,x,d);
	}

	ans += deld.size();

	// debug();
      }
    }

  }

  printf("%d\n", ans);

  return 0;
}

