const char dot = '.';
inline int to_index(char c){
    return c - 'A';
}
const vector<int> dy = {-1,0,1,0};
const vector<int> dx = {0,1,0,-1};
signed main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    int H,W; cin >> H >> W;
    vector<string> field(H);
    for(string& s : field){
        cin >> s;
    }

    set<tuple<int,int> > should_press;
    for(int y=0;y<H;y++){
        for(int x=0;x<W;x++){
            vector<int> v(26);
            for(int i=0;i<4;i++){
                for(int k=1;k<max(H,W);k++){
                    int ny = y + k * dy[i],
                        nx = x + k * dx[i];
                    if(ny < 0 or ny >= H or nx < 0 or nx >= W) break;
                    const char here = field[ny][nx];
                    if(here != dot){
                        v[to_index(here)]++;
                        // break;
                    }
                }
            }
            if(*max_element(all(v)) >= 2){
                should_press.emplace(y,x);
            }
        }
    }

    // dump(should_press);
    bool cont = true;
    int ret = 0;
    while(cont){
        cont = false;
        for(auto t : should_press){
            int y = get<0>(t),
                x = get<1>(t);
            if(field[y][x] != dot) continue;
            vector<tuple<int,int> > tiles;
            for(int i=0;i<4;i++){
                for(int k=1;k<max(H,W);k++){
                    int ny = y + k * dy[i],
                        nx = x + k * dx[i];
                    if(ny < 0 or ny >= H or nx < 0 or nx >= W) break;
                    const char here = field[ny][nx];
                    if(here != dot){
                        tiles.emplace_back(ny,nx);
                        break;
                    }
                }
            }
            for(int i=0;i<tiles.size();i++){
                for(int j=i+1;j<tiles.size();j++){
                    char& ic = field[get<0>(tiles[i])][get<1>(tiles[i])];
                    char& ij = field[get<0>(tiles[j])][get<1>(tiles[j])];
                    if(ic == ij){
                        ic = dot;
                        ij = dot;
                        ret+=2;
                        cont = true;
                        goto end;
                    }
                }
            }
        end:{}
        }
    }
    for(string s : field){
        cerr << s << endl;
    }
    cout << ret << endl;
    return 0;
}

#include<iostream>
#include<vector>
using namespace std;

int N,M,x[4]={1,0,-1,0},y[4]={0,1,0,-1};
char P[501][501];
vector<pair<char,int> > V;

int beat(int a,int b)
{
  int c,d;
  V.clear();
  for(int k=0;k<4;k++){
    c=a+x[k],d=b+y[k];
    while(1){
      if(c<0 || d<0 || c>=M || d>=N)break;
      if(P[c][d]!='.'){
	V.push_back(make_pair(P[c][d],k));
	break;
      }
      c+=x[k],d+=y[k];
    }
  }
  int sum=0;
  for(int i=0;i<V.size();i++){
    for(int j=i+1;j<V.size();j++){
      if(V[j].first==V[i].first){
        sum+=2;
	c=a+x[V[j].second],d=b+y[V[j].second];
        while(1){
          if(P[c][d]==V[j].first){
	    P[c][d]='.';
	    break;
	  }
	  c+=x[V[j].second],d+=y[V[j].second];
	}
	c=a+x[V[i].second],d=b+y[V[i].second];
        while(1){
          if(P[c][d]==V[i].first){
	    P[c][d]='.';
	    break;
	  }
	  c+=x[V[i].second],d+=y[V[i].second];
	}
      }
    }
  }       
  return sum;
}

int main()
{
  int ans=0,sum;
  cin>>M>>N;
  for(int i=0;i<M;i++){
    for(int j=0;j<N;j++)cin>>P[i][j];
  }
  while(1){
    sum=0;
    for(int i=0;i<M;i++){
      for(int j=0;j<N;j++){
	if(P[i][j]=='.')sum+=beat(i,j);
      }
    }
    if(sum==0)break;
    ans+=sum;
  }
  cout<<ans<<endl;
  return 0;
}

#include<bits/stdc++.h>
#define REP(i,n,N) for(int i=(n);i<(int)(N);i++)
#define p(s) cout<<(s)<<endl;
#define F first
#define S second
using namespace std;

string s[501];
bool tacX(int x1,int x2,int y,char c){
    for(int x=x1;x<=x2;x++) if(s[y][x]!='.' && s[y][x]!=c) return false;
    return true;
}

bool tacY(int y1,int y2,int x,char c){
    for(int y=y1;y<=y2;y++) if(s[y][x]!='.' && s[y][x]!=c) return false;
    return true;
}
int main(){
	int M,N;
	cin>>M>>N;
	vector<pair<int,int>> vc[30];
	REP(i,0,M){
		cin>>s[i];
		REP(j,0,N){
			if(s[i][j]!='.') vc[s[i][j]-'A'].push_back({j,i});
		}
	}
	int ans=0;
	while(1){
		bool change=false;
		REP(i,0,'Z'-'A'+1){
			if(vc[i].size()!=2) continue;
			pair<int,int> a=vc[i][0],b=vc[i][1];
			bool ok=false;
			if(a.F!=b.F&&a.S!=b.S){
				if((tacX(min(a.F,b.F),max(a.F,b.F),a.S,'A'+i)
				&& tacY(a.S,b.S,b.F,'A'+i))
				|| (tacX(min(a.F,b.F),max(a.F,b.F),b.S,'A'+i)
				&& tacY(a.S,b.S,a.F,'A'+i))) ok=true;
			}
			else if(a.S==b.S&&b.F-a.F>1&& tacX(a.F,b.F,a.S,'A'+i)) ok=true;
			else if(a.F==b.F&&b.S-a.S>1&&tacY(a.S,b.S,a.F,'A'+i)) ok=true;
			if(ok){
				s[a.S][a.F]=s[b.S][b.F]='.';
				vc[i].clear();
				ans+=2;
				change=true;
			}
		}
		if(!change) break;
	}
	p(ans);
	return 0;
}

