#include <cstdio>
#include <cstring>
#include <string>
#include <vector>
#include <iostream>
#include <algorithm>
using namespace std;

typedef pair<int,int> P;

int m,n;
vector<P> data[30];
vector<int> xindex;
vector<int> yindex;
int fie[60][60];
int dx[4]={1,0,-1,0};
int dy[4]={0,1,0,-1};
int pick[4];
int pickx[4];
int picky[4];

int main(void){
	scanf("%d %d",&n,&m);
	for(int i=0;i<n;i++){
		string str;
		cin >> str;
		for(int j=0;j<m;j++){
			if(str[j]>='A' && str[j]<='Z'){
				data[str[j]-'A'+1].push_back(P(j,i));
				xindex.push_back(j);
				yindex.push_back(i);
			}
		}
	}
	sort(xindex.begin(),xindex.end());
	xindex.erase(unique(xindex.begin(),xindex.end()),xindex.end());
	sort(yindex.begin(),yindex.end());
	yindex.erase(unique(yindex.begin(),yindex.end()),yindex.end());
	for(int i=1;i<=26;i++){
		for(int j=0;j<data[i].size();j++){
			data[i][j].first=lower_bound(xindex.begin(),xindex.end(),data[i][j].first)-xindex.begin();
			data[i][j].second=lower_bound(yindex.begin(),yindex.end(),data[i][j].second)-yindex.begin();
			fie[data[i][j].first][data[i][j].second]=i;
		}
	}
	bool flag=true;
	int score=0;
	while(flag){
		flag=false;
		for(int i=0;i<yindex.size();i++){
			for(int j=0;j<xindex.size();j++){
				if(fie[j][i]==0){
					memset(pick,0,sizeof(pick));
					for(int k=0;k<4;k++){
						int x=j,y=i;
						while((x>=0 && x<xindex.size() && y>=0 && y<yindex.size()) && fie[x][y]==0){
							x+=dx[k];
							y+=dy[k];
						}
						if(fie[x][y]!=0){
							pick[k]=fie[x][y];
							pickx[k]=x;
							picky[k]=y;
						}
					}
					for(int k=0;k<4;k++){
						for(int l=k+1;l<4;l++){
							if(pick[k]==pick[l] && pick[k]!=0){
								score+=2;
								fie[pickx[k]][picky[k]]=0;
								fie[pickx[l]][picky[l]]=0;
								flag=true;
							}
						}
					}
				}
			}
		}
	}
	printf("%d\n",score);
	return 0;
}

#include <bits/stdc++.h>

using namespace std;

#define MAX_H 500
#define MAX_W 500
#define F first
#define S second
typedef pair<int,int> pii;

int H,W;
char field[MAX_H][MAX_W];
vector<pii> v[26];

bool inField(int x,int y){
    return (0 <= x && x < W && 0 <= y && y < H);
}

int calc(int x,int y){
    for(int i = 0 ; i < 26 ; i++){
        v[i].clear();
    }
    const int dx[] = {-1,0,1,0};
    const int dy[] = {0,-1,0,1};
    for(int i = 0 ; i < 4 ; i++){
        int nx = x, ny = y;
        while(1){
            nx += dx[i]; ny += dy[i];
            if(!inField(nx,ny)) break;
            if(field[ny][nx] != '.'){
                v[field[ny][nx]-'A'].push_back(pii(nx,ny));
                break;
            }
        }
    }
    int res = 0;
    for(int i = 0 ; i < 26 ; i++){
        if(v[i].size() == 2){
            field[v[i][0].S][v[i][0].F] = '.';
            field[v[i][1].S][v[i][1].F] = '.';
            res += 2;
        }
    }
    return res;
}

int main(){
    cin >> H >> W;
    for(int i = 0 ; i < H ; i++){
        for(int j = 0 ; j < W ; j++){
            cin >> field[i][j];
        }
    }
