};

bool operator<(const data &l, const data &r) {
  if(l.x1 != r.x1) return l.x1 < r.x1;
  if(l.x2 != r.x2) return l.x2 < r.x2;
  if(l.y1 != r.y1) return l.y1 < r.y1;
  return l.y2 < r.y2;
}

long long m, n;
