long long solve() {
  int ans = 0;
  for(int i = 0; i < m; ++i)
    for(int j = 0; j < n; ++j)
      if(c[i][j] != '.') {
        if(mp.find(c[i][j]) != mp.end()) {
          mp[c[i][j]].x2 = i;
          mp[c[i][j]].y2 = j;
        }
        else {
          mp[c[i][j]].x1 = i;
          mp[c[i][j]].y1 = j;
        }
      }
  for(int T = 0; T < 30; ++T) {
    for(auto nowmp : mp)
      if(st.find(nowmp.first) == st.end()) {
        data now = nowmp.second;
        if(ch(now)) {
          c[now.x1][now.y1] = c[now.x2][now.y2] = '.';
          ans += 2;
          st.insert(nowmp.first);
        }
      }
  }
  return ans;
}

bool ch(data now) {
  if(now.x1 == now.x2)
    return checkx(now.x1, min(now.y1, now.y2),
                  max(now.y1, now.y2));
  else if(now.y1 == now.y2) {
    return checky(now.y1, min(now.x1, now.x2),
                  max(now.x1, now.x2));
  }
  else {
    if((min(now.y1, now.y2) + 1 == max(now.y1, now.y2) ||
        checkx(now.x1, min(now.y1, now.y2),
               max(now.y1, now.y2))) &&
       (min(now.x1, now.x2) + 1 == max(now.x1, now.x2) ||
        checky(now.y2, min(now.x1, now.x2),
               max(now.x1, now.x2))) &&
       c[now.x1][now.y2] == '.')
      return 1;
    return (min(now.y1, now.y2) + 1 ==
                max(now.y1, now.y2) ||
            checkx(now.x2, min(now.y1, now.y2),
                   max(now.y1, now.y2))) &&
           (min(now.x1, now.x2) + 1 ==
                max(now.x1, now.x2) ||
            checky(now.y1, min(now.x1, now.x2),
                   max(now.x1, now.x2))) &&
           c[now.x2][now.y1] == '.';
  }
}

bool checkx(int x, int d, int u) {
  bool res = 0;
  for(int i = d + 1; i < u; ++i) {
    if(i == d + 1) res = 1;
    if(c[x][i] != '.') res = 0;
  }
  return res;
}
bool checky(int y, int l, int r) {
  bool res = 0;
  for(int i = l + 1; i < r; ++i) {
    if(i == l + 1) res = 1;
    if(c[i][y] != '.') res = 0;
  }
  return res;
}

