vector<i_i> g(int h, int w) {
    vector<i_i> ret;
    while(Up[h][w] > 0) {
        if(field[Up[h][w]][w] != '.') {
            ret.push_back({Up[h][w], w});
            break;
        }
        Up[h][w]--;
    }
    while(Down[h][w] <= H) {
        if(field[Down[h][w]][w] != '.') {
            ret.push_back({Down[h][w], w});
            break;
        }
        Down[h][w]++;
    }
    while(Left[h][w] > 0) {
        if(field[h][Left[h][w]] != '.') {
            ret.push_back({h, Left[h][w]});
            break;
        }
        Left[h][w]--;
    }
    while(Right[h][w] <= W) {
        if(field[h][Right[h][w]] != '.') {
            ret.push_back({h, Right[h][w]});
            break;
        }
        Right[h][w]++;
    }
    return ret;
