	static class FastScanner {
	    private final InputStream in = System.in;
	    private final byte[] buffer = new byte[1024];
	    private int ptr = 0;
	    private int buflen = 0;
	    private boolean hasNextByte() {
	        if (ptr < buflen) {
	            return true;
	        }else{
	            ptr = 0;
	            try {
	                buflen = in.read(buffer);
	            } catch (IOException e) {
	                e.printStackTrace();
	            }
	            if (buflen <= 0) {
	                return false;
	            }
	        }
	        return true;
	    }
	    private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
	    private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
	    private void skipUnprintable() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;}
	    public boolean hasNext() { skipUnprintable(); return hasNextByte();}
	    public String next() {
	        if (!hasNext()) throw new NoSuchElementException();
	        StringBuilder sb = new StringBuilder();
	        int b = readByte();
	        while(isPrintableChar(b)) {
	            sb.appendCodePoint(b);
	            b = readByte();
	        }
	        return sb.toString();
	    }
	    public long nextLong() {
	        if (!hasNext()) throw new NoSuchElementException();
	        long n = 0;
	        boolean minus = false;
	        int b = readByte();
	        if (b == '-') {
	            minus = true;
	            b = readByte();
	        }
	        if (b < '0' || '9' < b) {
	            throw new NumberFormatException();
	        }
	        while(true){
	            if ('0' <= b && b <= '9') {
	                n *= 10;
	                n += b - '0';
	            }else if(b == -1 || !isPrintableChar(b)){
	                return minus ? -n : n;
	            }else{
	                throw new NumberFormatException();
	            }
	            b = readByte();
	        }
	    }
	    public int nextInt() {
	        if (!hasNext()) throw new NoSuchElementException();
	        int n = 0;
	        boolean minus = false;
	        int b = readByte();
	        if (b == '-') {
	            minus = true;
	            b = readByte();
	        }
	        if (b < '0' || '9' < b) {
	            throw new NumberFormatException();
	        }
	        while(true){
	            if ('0' <= b && b <= '9') {
	                n *= 10;
	                n += b - '0';
	            }else if(b == -1 || !isPrintableChar(b)){
	                return minus ? -n : n;
	            }else{
	                throw new NumberFormatException();
	            }
	            b = readByte();
	        }
	    }
	}
}

import java.io.*;
import java.util.*;

public class Main {
	FastScanner in = new FastScanner(System.in);
	PrintWriter out = new PrintWriter(System.out);

	public void run() {
		int n = in.nextInt(), m = in.nextInt();

		char[][] map = new char[n][];
		for (int i = 0; i < n; i++) {
			map[i] = in.next().toCharArray();
		}

		int[] X1 = new int[26];
		int[] Y1 = new int[26];
		int[] X2 = new int[26];
		int[] Y2 = new int[26];
		for (int c = 'A'; c <= 'Z'; c++) {
			int x1 = -1, y1 = -1, x2 = -1, y2 = -1;
			for (int i = 0; i < n; i++) {
				for (int j = 0; j < m; j++) {
					if (map[i][j] == c) {
						if (x1 == -1 && y1 == -1) {
							x1 = j; y1 = i;
						} else {
							x2 = j; y2 = i;
						}
					}
				}
			}

			if (x1 != -1 && Math.abs(x1 - x2) + Math.abs(y1 - y2) != 1) {
				if (x1 > x2) {
					int t = x1;
					x1 = x2;
					x2 = t;
					t = y1;
					y1 = y2;
					y2 = t;
				}

				X1[c - 'A'] = x1;
				X2[c - 'A'] = x2;
				Y1[c - 'A'] = y1;
				Y2[c - 'A'] = y2;
			}
		}

		int res = 0;
		boolean more = true;
		while (more) {
			more = false;
			for (int cc = 'A'; cc <= 'Z'; cc++) {
				int c = cc - 'A';
				if (X1[c] == 0 && Y1[c] == 0 && X2[c] == 0 && Y2[c] == 0) continue;

				boolean ok1 = true, ok2 = true;
				if (Y1[c] < Y2[c]) {
					for (int y = Y1[c]; y <= Y2[c]; y++) {
						if (map[y][X1[c]] != '.' && map[y][X1[c]] - 'A' != c) ok1 = false;
						if (map[y][X2[c]] != '.' && map[y][X2[c]] - 'A' != c) ok2 = false;					
					}
					for (int x = X1[c]; x <= X2[c]; x++) {
						if (map[Y1[c]][x] != '.' && map[Y1[c]][x] - 'A' != c) ok2 = false;
						if (map[Y2[c]][x] != '.' && map[Y2[c]][x] - 'A' != c) ok1 = false;
					}
				} else {
					for (int y = Y2[c]; y <= Y1[c]; y++) {
						if (map[y][X1[c]] != '.' && map[y][X1[c]] - 'A' != c) ok1 = false;
						if (map[y][X2[c]] != '.' && map[y][X2[c]] - 'A' != c) ok2 = false;					
					}
					for (int x = X1[c]; x <= X2[c]; x++) {
						if (map[Y1[c]][x] != '.' && map[Y1[c]][x] - 'A' != c) ok2 = false;
						if (map[Y2[c]][x] != '.' && map[Y2[c]][x] - 'A' != c) ok1 = false;
					}					
				}
				if (ok1 || ok2) {
					map[Y1[c]][X1[c]] = '.';
					map[Y2[c]][X2[c]] = '.';
					X1[c] = Y1[c] = X2[c] = Y2[c] = 0;
					more = true;
					res += 2;
				}
			}			
		}
		System.out.println(res);

		out.close();
	}

	public static void main(String[] args) {
		new Main().run();
	}

