	class FastScanner {
		private InputStream stream;
		private byte[] buf = new byte[1024];
		private int curChar;
		private int numChars;

		public FastScanner(InputStream stream) {
			this.stream = stream;
			//stream = new FileInputStream(new File("dec.in"));

		}

		int read() {
			if (numChars == -1)
				throw new InputMismatchException();
			if (curChar >= numChars) {
				curChar = 0;
				try {
					numChars = stream.read(buf);
				} catch (IOException e) {
					throw new InputMismatchException();
				}
				if (numChars <= 0)
					return -1;
			}
			return buf[curChar++];
		}

		boolean isSpaceChar(int c) {
			return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
		}

		boolean isEndline(int c) {
			return c == '\n' || c == '\r' || c == -1;
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		int[] nextIntArray(int n) {
			int[] array = new int[n];
			for (int i = 0; i < n; i++)
				array[i] = nextInt();

			return array;
		}

		int[][] nextIntMap(int n, int m) {
			int[][] map = new int[n][m];
			for (int i = 0; i < n; i++) {
				map[i] = in.nextIntArray(m);
			}
			return map;
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		long[] nextLongArray(int n) {
			long[] array = new long[n];
			for (int i = 0; i < n; i++)
				array[i] = nextLong();

			return array;
		}

		long[][] nextLongMap(int n, int m) {
			long[][] map = new long[n][m];
			for (int i = 0; i < n; i++) {
				map[i] = in.nextLongArray(m);
			}
			return map;
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

		double[] nextDoubleArray(int n) {
			double[] array = new double[n];
			for (int i = 0; i < n; i++)
				array[i] = nextDouble();

			return array;
		}

		double[][] nextDoubleMap(int n, int m) {
			double[][] map = new double[n][m];
			for (int i = 0; i < n; i++) {
				map[i] = in.nextDoubleArray(m);
			}
			return map;
		}

		String next() {
			int c = read();
			while (isSpaceChar(c))
				c = read();
			StringBuilder res = new StringBuilder();
			do {
				res.appendCodePoint(c);
				c = read();
			} while (!isSpaceChar(c));
			return res.toString();
		}

		String[] nextStringArray(int n) {
			String[] array = new String[n];
			for (int i = 0; i < n; i++)
				array[i] = next();

			return array;
		}

		String nextLine() {
			int c = read();
			while (isEndline(c))
				c = read();
			StringBuilder res = new StringBuilder();
			do {
				res.appendCodePoint(c);
				c = read();
			} while (!isEndline(c));
			return res.toString();
		}
	}
}

import java.util.*;
import java.lang.*;
import java.math.*;

public class Main {
	Scanner sc = new Scanner(System.in);

	void run() {
		int h = sc.nextInt();
		int w = sc.nextInt();

		char map[][] = new char[h][];

		for (int i = 0; i < h; i++) {
			map[i] = sc.next().toCharArray();
		}

		int mask[][] = new int[h][w];
		int max = 0;

		int y[] = new int[h];
		int t[] = new int[w];

		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if (map[i][j] == '.') {
					mask[i][j] = 0;
				} else {
					mask[i][j] = 1 << (map[i][j] - 'A');
					max = Math.max(map[i][j] - 'A', max);
				}
				y[i] |= mask[i][j];
				t[j] |= mask[i][j];
			}
		}
		max += 1;
		boolean dp[] = new boolean[1 << max];
		dp[0] = true;
		int n = max;
		int mk = 0;

		int nnt[] = new int[n];
		int nnt2[] = new int[n];
		int nny[] = new int[n];
		int nny2[] = new int[n];

		int memo[] = new int[n];
		Arrays.fill(memo, -1);
		int memo2[] = new int[n];
		Arrays.fill(memo, -1);

		for (int j = 0; j < n; j++) {
			int shj = 1 << j;

			int nt = -1;
			int nt2 = -1;
			int ny = -1;
			int ny2 = -1;
			for (int ii = 0; ii < h; ii++) {
				if ((y[ii] & shj) != 0) {
					if (nt == -1) {
						nt = ii;
					} else {
						nt2 = ii;
					}
				}
			}
			for (int ii = 0; ii < w; ii++) {
				if ((t[ii] & shj) != 0) {
					if (ny == -1) {
						ny = ii;
					} else {
						ny2 = ii;
					}
				}
			}
			if (nt2 == -1) {
				nt2 = nt;
			}
			if (ny2 == -1) {
				ny2 = ny;
			}

			if ((nt & ny) == -1) {
				nnt[j] = nt;
				nnt2[j] = nt2;
				nny[j] = ny;
				nny2[j] = ny2;
				continue;
			}

			if (mask[nt][ny] == shj) {
				int temp = ny2;
				ny2 = ny;
				ny = temp;
			}

			nnt[j] = nt;
			nnt2[j] = nt2;
			nny[j] = ny;
			nny2[j] = ny2;

			int tf = 0;
			int tf2 = 0;
			for (int ii = Math.min(nt, nt2); ii <= Math.max(nt, nt2); ii++) {
				tf |= mask[ii][ny];
				tf2 |= mask[ii][ny2];
			}

			int yf = 0;
			int yf2 = 0;
			for (int ii = Math.min(ny, ny2); ii <= Math.max(ny, ny2); ii++) {
				yf |= mask[nt][ii];
				yf2 |= mask[nt2][ii];

			}

			memo[j] = tf | yf;
			memo2[j] = tf2 | yf2;

		}

		int i = 0;
		for (;;) {
			int previ = i;			
			for (int j = 0; j < n; j++) {
				int shj = 1 << j;
				if (dp[i | shj]) {
					// System.out.println("here");
					continue;
				}

				int nt = nnt[j];
				int nt2 = nnt2[j];
				int ny = nny[j];
				int ny2 = nny2[j];

				if ((nt & ny) == -1) {
					continue;
				}
				if (Math.abs(nt - nt2) + Math.abs(ny2 - ny) < 2) {
					continue;
				}

				if (((~(i | shj)) & (memo[j])) == 0
						|| ((~(i | shj)) & (memo2[j])) == 0) {
					dp[i | shj] = true;
					i |= shj;
				}
			}
			if(i==previ){
				break;

			}
		}
		System.out.println(Integer.bitCount(i)*2);
	}

	public static void main(String[] args) {
		Main m = new Main();
		m.run();
	}
}

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {

		new Main().run();
		return;
	}

	Scanner scanner = new Scanner(System.in);
	int m;
	int[][][] tile = new int[26][2][2];
	boolean[] cantGet =new boolean[26];
	int score = 0;

	void run() {

		m = scanner.nextInt();
		scanner.nextInt();

		for (int color = 0; color < 26; color ++) {

			tile[color][0][0] = -1;
			cantGet[color] = true;
		}

		for (int y = 0; y < m; y ++) {

			char[] line = scanner.next().toCharArray();

			for (int x = 0; x < line.length; x ++) {

				if(line[x] != '.') {

					int color = line[x] - 'A';
					cantGet[color] = false;
					int index = (tile[color][0][0] == -1) ? 0: 1;
					tile[color][index][0] = x;
					tile[color][index][1] = y;
				}
			}
		}

		int preScore;
		do {

			preScore = score;

			for (int color = 0; color < 26; color ++) {

				tryGet(color);
			}

		} while(preScore != score);

		System.out.println(score * 2);
	}

	void tryGet(int color) {

		if (cantGet[color]) {

			return;
		}

		if ((tile[color][0][0] == tile[color][1][0]
				&& Math.abs(tile[color][0][1] - tile[color][1][1]) == 1)
				|| (tile[color][0][1] == tile[color][1][1]
				&& Math.abs(tile[color][0][0] - tile[color][1][0]) == 1)) {

			cantGet[color] = true;
			return;
		}

		if((isEmpty(color, 1, tile[color][0][0], tile[color][0][1], tile[color][1][1])
				&& isEmpty(color, 0, tile[color][1][1], tile[color][0][0], tile[color][1][0]))
				|| (isEmpty(color, 1, tile[color][1][0], tile[color][0][1], tile[color][1][1])
						&& isEmpty(color, 0, tile[color][0][1], tile[color][0][0], tile[color][1][0]))) {

			get(color);
		}
	}

	boolean isEmpty(int skip,int axis, int point, int start, int end) {


		if (start == end) {

			return true;
		}

		if (start > end) {

			int swap = start;
			start = end;
			end = swap;
		}

		for (int color = 0; color < 26; color ++) {

			if (color == skip
					|| tile[color][0][0] == -1) {

				continue;
			}

			for (int index = 0; index < 2; index ++) {

				if (tile[color][index][1 - axis] == point
						&& tile[color][index][axis] >= start
						&& tile[color][index][axis] <= end) {

					return false;
				}
			}
		}

		return true;
	}

	void get(int color) {

		tile[color][0][0] = -1;
		score ++;
	}
}

