import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Collection;
import java.util.Deque;
import java.util.Scanner;

public class Main {

	public static void main(String[] args) {

		new Main().run();
	}

	Scanner scanner = new Scanner(System.in);
	int m;
	int[][][] tile = new int[26][2][2];
	Deque<Integer> que = new ArrayDeque<Integer>();
	boolean[] cantGet =new boolean[26];
	boolean[] check =new boolean[26];
	int score = 0;

	void run() {

		m = scanner.nextInt();
		scanner.nextInt();

		for (int color = 0; color < tile.length; color ++) {

			tile[color][0][0] = -1;
			cantGet[color] = false;
			check[color] = false;
		}

		for (int y = 0; y < m; y ++) {

			char[] line = scanner.next().toCharArray();

			for (int x = 0; x < line.length; x ++) {

				if(line[x] != '.') {

					int color = line[x] - 'A';
					int index = (tile[color][0][0] == -1) ? 0: 1;
					tile[color][index][0] = x;
					tile[color][index][1] = y;
				}
			}
		}

		long s = System.currentTimeMillis();
		for (int color = 0; color < tile.length; color ++) {

			if (tile[color][0][0] != -1
					&& ! tryGet(color) && ! que.isEmpty()) {

				bfs();
			}
		}
		System.out.println(System.currentTimeMillis()-s);

		System.out.println(score * 2);
	}

	Collection<Integer> old = new ArrayList<Integer>(que);

	void bfs() {

		do {

			old.clear();
			old.addAll(que);
			int color;
			int last = que.getLast();
			do {

				color = que.remove();
				tryGet(color);

			} while (color != last);

		} while (! que.isEmpty() && ! old.containsAll(que));

		if (! que.isEmpty()) {

			for (int color : que) {

				cantGet[color] = true;
			}
		}
	}

	boolean tryGet(int color) {

		if (cantGet[color]) {

			return false;
		}

		if (! check[color]) {

			check[color] = true;
			if ((tile[color][0][0] == tile[color][1][0]
					&& Math.abs(tile[color][0][1] - tile[color][1][1]) == 1)
					|| (tile[color][0][1] == tile[color][1][1]
					&& Math.abs(tile[color][0][0] - tile[color][1][0]) == 1)) {

				cantGet[color] = true;
				return false;
			}
		}

		if((isEmpty(color, 1, tile[color][0][0], tile[color][0][1], tile[color][1][1])
					&& isEmpty(color, 0, tile[color][1][1], tile[color][0][0], tile[color][1][0]))
				|| (isEmpty(color, 1, tile[color][1][0], tile[color][0][1], tile[color][1][1])
					&& isEmpty(color, 0, tile[color][0][1], tile[color][0][0], tile[color][1][0]))) {

			tile[color][0][0] = -1;
			score ++;
			return true;
		}

		que.add(color);
		return false;
	}

	boolean isEmpty(int skip,int axis, int point, int start, int end) {

		if (start == end) {

			return true;
		}

		if (start > end) {

			int swap = start;
			start = end;
			end = swap;
		}

		boolean isEmpty = true;
		for (int color = 0; color < tile.length; color ++) {

			if (color == skip
					|| tile[color][0][0] == -1) {

				continue;
			}

			for (int index = 0; index < tile[0].length; index ++) {

				if (tile[color][index][1 - axis] == point
						&& tile[color][index][axis] >= start
						&& tile[color][index][axis] <= end) {

					isEmpty = false;
					if (! que.contains(color)
							&& ! cantGet[color]) {

						que.add(color);
					}
					break;
				}
			}
		}

		return isEmpty;
	}
}

import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Collection;
import java.util.Deque;
import java.util.Scanner;

public class Main {

	public static void main(String[] args) {

		new Main().run();
	}

	Scanner scanner = new Scanner(System.in);
	int m;
	int[][][] tile = new int[26][2][2];
	Deque<Integer> que = new ArrayDeque<Integer>();
	boolean[] cantGet =new boolean[26];
	boolean[] check =new boolean[26];
	int score = 0;

	void run() {

		m = scanner.nextInt();
		scanner.nextInt();

		for (int color = 0; color < tile.length; color ++) {

			tile[color][0][0] = -1;
			cantGet[color] = false;
			check[color] = false;
		}

		for (int y = 0; y < m; y ++) {

			char[] line = scanner.next().toCharArray();

			for (int x = 0; x < line.length; x ++) {

				if(line[x] != '.') {

					int color = line[x] - 'A';
					int index = (tile[color][0][0] == -1) ? 0: 1;
					tile[color][index][0] = x;
					tile[color][index][1] = y;
				}
			}
		}

		for (int color = 0; color < tile.length; color ++) {

			if (tile[color][0][0] != -1
					&& ! tryGet(color) && ! que.isEmpty()) {

				bfs();
			}
		}

		System.out.println(score * 2);
	}

	Collection<Integer> old = new ArrayList<Integer>(que);

	void bfs() {

		do {

			old.clear();
			old.addAll(que);
			int color;
			int last = que.getLast();
			do {

				color = que.remove();
				tryGet(color);

			} while (color != last);

		} while (! que.isEmpty() && ! old.containsAll(que));

		if (! que.isEmpty()) {

			for (int color : que) {

				cantGet[color] = true;
			}
		}
	}

	boolean tryGet(int color) {

		if (cantGet[color]) {

			return false;
		}

		if (! check[color]) {

			check[color] = true;
			if ((tile[color][0][0] == tile[color][1][0]
					&& Math.abs(tile[color][0][1] - tile[color][1][1]) == 1)
					|| (tile[color][0][1] == tile[color][1][1]
					&& Math.abs(tile[color][0][0] - tile[color][1][0]) == 1)) {

				cantGet[color] = true;
				return false;
			}
		}

		if((isEmpty(color, 1, tile[color][0][0], tile[color][0][1], tile[color][1][1])
					&& isEmpty(color, 0, tile[color][1][1], tile[color][0][0], tile[color][1][0]))
				|| (isEmpty(color, 1, tile[color][1][0], tile[color][0][1], tile[color][1][1])
					&& isEmpty(color, 0, tile[color][0][1], tile[color][0][0], tile[color][1][0]))) {

			tile[color][0][0] = -1;
			score ++;
			return true;
		}

		que.add(color);
		return false;
	}

	boolean isEmpty(int skip,int axis, int point, int start, int end) {

		if (start == end) {

			return true;
		}

		if (start > end) {

			int swap = start;
			start = end;
			end = swap;
		}

		boolean isEmpty = true;
		for (int color = 0; color < tile.length; color ++) {

			if (color == skip
					|| tile[color][0][0] == -1) {

				continue;
			}

			for (int index = 0; index < tile[0].length; index ++) {

				if (tile[color][index][1 - axis] == point
						&& tile[color][index][axis] >= start
						&& tile[color][index][axis] <= end) {

					isEmpty = false;
					if (! que.contains(color)
							&& ! cantGet[color]) {

						que.add(color);
					}
					break;
				}
			}
		}

		return isEmpty;
	}
}

import java.util.*;
import java.lang.*;
import java.math.*;
import java.io.*;
import static java.lang.Math.*;
import static java.util.Arrays.*;

public class Main{
	Scanner sc=new Scanner(System.in);

	int INF=1<<28;
	double EPS=1e-12;

	int n, m;
	char[][] map;
	boolean[][] removed;
	int ans;
	int type='Z'-'A'+1;
	LinkedList<P> pList;

	void run(){
		n=sc.nextInt();
		m=sc.nextInt();
		map=new char[n][];
		for(int j=0; j<n; j++){
			map[j]=sc.next().toCharArray();
		}

		@SuppressWarnings("unchecked")
		LinkedList<P>[] lists=new LinkedList[type];
		for(int i=0; i<type; i++){
			lists[i]=new LinkedList<P>();
		}
		for(int j=0; j<n; j++){
			for(int i=0; i<m; i++){
				if(map[j][i]!='.'){
					lists[map[j][i]-'A'].add(new P(i, j));
				}
			}
		}

		pList=new LinkedList<P>();
		for(int i=0; i<type; i++){
			if(lists[i].size()==2){
				P p1=lists[i].get(0);
				P p2=lists[i].get(1);
				if(p1.x==p2.x){
					for(int y=min(p1.y, p2.y)+1; y<max(p1.y, p2.y); y++){
						pList.add(new P(p1.x, y));
					}
				}else if(p1.y==p2.y){
					for(int x=min(p1.x, p2.x)+1; x<max(p1.x, p2.x); x++){
						pList.add(new P(x, p1.y));
					}
				}else{
					pList.add(new P(p1.x, p2.y));
					pList.add(new P(p2.x, p1.y));
				}
			}
		}

		int[] dx={0, 0, -1, 1};
		int[] dy={-1, 1, 0, 0};

		ans=0;
		removed=new boolean[n][m];
		for(;;){
			int s=0;
			for(P p : pList){
				if(map[p.y][p.x]=='.'||removed[p.y][p.x]){}else{
					continue;
				}
				P[] ps=new P[4];
				for(int k=0; k<4; k++){
					ps[k]=get(p.x, p.y, dx[k], dy[k]);
				}
				for(int j=0; j<4; j++){
					for(int i=j+1; i<4; i++){
						if(ps[i]!=null&&ps[j]!=null
								&&map[ps[i].y][ps[i].x]==map[ps[j].y][ps[j].x]){
							removed[ps[i].y][ps[i].x]=true;
							removed[ps[j].y][ps[j].x]=true;
							s+=2;
						}
					}
				}
			}
			ans+=s;
			if(s==0){
				break;
			}
		}
		println(ans+"");
	}

	P get(int x, int y, int dx, int dy){
		for(x+=dx, y+=dy; x>=0&&x<m&&y>=0&&y<n; x+=dx, y+=dy){
			if(!removed[y][x]&&map[y][x]!='.'){
				return new P(x, y);
			}
		}
		return null;
	}

	class P{
		int x, y;

		P(int x, int y){
			this.x=x;
			this.y=y;
		}

		@Override
		public String toString(){
			return "("+x+","+y+")";
		}
	}

	void debug(Object... os){
		System.err.println(Arrays.deepToString(os));
	}

	void print(String s){
		System.out.print(s);
	}

	void println(String s){
		System.out.println(s);
	}

	public static void main(String[] args){
		// System.setOut(new PrintStream(new BufferedOutputStream(System.out)));
		new Main().run();
	}
}

import java.util.*;
import java.lang.*;
import java.math.*;

public class Main {
	Scanner sc = new Scanner(System.in);

	void run() {
		int h = sc.nextInt();
		int w = sc.nextInt();

		char map[][] = new char[h][];

		for (int i = 0; i < h; i++) {
			map[i] = sc.next().toCharArray();
		}

		int mask[][] = new int[h][w];
		int max = 0;

		int y[] = new int[h];
		int t[] = new int[w];

		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if (map[i][j] == '.') {
					mask[i][j] = 0;
				} else {
					mask[i][j] = 1 << (map[i][j] - 'A');
					max = Math.max(map[i][j] - 'A', max);
				}
				y[i] |= mask[i][j];
				t[j] |= mask[i][j];
			}
		}
		max += 1;
		boolean dp[] = new boolean[1 << max];
		dp[0] = true;
		int n = max;
		int mk = 0;

		int nnt[] = new int[n];
		int nnt2[] = new int[n];
		int nny[] = new int[n];
		int nny2[] = new int[n];

		int memo[] = new int[n];
		Arrays.fill(memo, -1);
		int memo2[] = new int[n];
		Arrays.fill(memo, -1);

		for (int j = 0; j < n; j++) {
			int shj = 1 << j;

			int nt = -1;
			int nt2 = -1;
			int ny = -1;
			int ny2 = -1;
			for (int ii = 0; ii < h; ii++) {
				if ((y[ii] & shj) != 0) {
					if (nt == -1) {
						nt = ii;
					} else {
						nt2 = ii;
					}
				}
			}
			for (int ii = 0; ii < w; ii++) {
				if ((t[ii] & shj) != 0) {
					if (ny == -1) {
						ny = ii;
					} else {
						ny2 = ii;
					}
				}
			}
			if (nt2 == -1) {
				nt2 = nt;
			}
			if (ny2 == -1) {
				ny2 = ny;
			}

			if ((nt & ny) == -1) {
				nnt[j] = nt;
				nnt2[j] = nt2;
				nny[j] = ny;
				nny2[j] = ny2;
				continue;
			}

			if (mask[nt][ny] == shj) {
				int temp = ny2;
				ny2 = ny;
				ny = temp;
			}

			nnt[j] = nt;
			nnt2[j] = nt2;
			nny[j] = ny;
			nny2[j] = ny2;

			int tf = 0;
			int tf2 = 0;
			for (int ii = Math.min(nt, nt2); ii <= Math.max(nt, nt2); ii++) {
				tf |= mask[ii][ny];
				tf2 |= mask[ii][ny2];
			}

			int yf = 0;
			int yf2 = 0;
			for (int ii = Math.min(ny, ny2); ii <= Math.max(ny, ny2); ii++) {
				yf |= mask[nt][ii];
				yf2 |= mask[nt2][ii];

			}

			memo[j] = tf | yf;
			memo2[j] = tf2 | yf2;

		}
		/*
		 * System.out.println(nnt['Z'-'A']+" "+nnt2['Z'-'A']);
		 * System.out.println(nny['Z'-'A']+" "+nny2['Z'-'A']);
		 * System.out.println("-----------------");
		 * System.out.println(nnt['U'-'A']+" "+nnt2['U'-'A']);
		 * System.out.println(nny['U'-'A']+" "+nny2['U'-'A']);
		 * System.out.println("-----------------");
		 */

		for (int i = 0; i < 1 << n; i++) {
			if (!dp[i]) {
				continue;
			}
			for (int j = 0; j < n; j++) {
				int shj = 1 << j;
				if ((i & shj) != 0) {
					continue;
				}

				int nt = nnt[j];
				int nt2 = nnt2[j];
				int ny = nny[j];
				int ny2 = nny2[j];

				if ((nt & ny) == -1) {
					continue;
				}
				if (Math.abs(nt - nt2) + Math.abs(ny2 - ny) < 2) {
					continue;
				}

				boolean ok = true;

				if (((~(i | shj)) & (memo[j])) == 0
						|| ((~(i | shj)) & (memo2[j])) == 0) {
					ok = true;
				} else {
					ok = false;
				}

				if (ok) {
//					 System.out.println(i + " " + Integer.bitCount(i) + " " +
//					 (char) (j + 'A'));
					 dp[i|shj]=true;
					 mk = Math.max(Integer.bitCount(i|shj), mk);
				}
			}
		}
		System.out.println(mk * 2);
	}

	public static void main(String[] args) {
		Main m = new Main();
		m.run();
	}
}

import java.util.Scanner;

//Tiles are Colorful
public class Main{

	void run(){
		Scanner sc = new Scanner(System.in);
		int h = sc.nextInt();
		int w = sc.nextInt();
		char[][] map = new char[h][w];
		for(int i=0;i<h;i++)map[i] = sc.next().toCharArray();
		boolean[] exist = new boolean[26];
		int[] num = new int[26];
		int[][][] pos = new int[26][2][2];
		for(int i=0;i<h;i++){
			for(int j=0;j<w;j++){
				if(map[i][j]=='.')continue;
				int id = map[i][j]-'A';
				exist[id] = true;
				pos[id][num[id]][0] = i;
				pos[id][num[id]][1] = j;
				num[id]++;
			}
		}
		long[][] cond = new long[26][2];
		for(int i=0;i<26;i++){
			if(!exist[i])continue;
			if(pos[i][0][0]==pos[i][1][0]){
				cond[i][1] = -1;
				if(Math.abs(pos[i][0][1]-pos[i][1][1])==1)cond[i][0] = -1;
				else{
					long s = 0;
					for(int j=pos[i][0][1]+1;j<pos[i][1][1];j++){
						char ch = map[pos[i][0][0]][j];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					cond[i][0] = s;
				}
			}
			else if(pos[i][0][1]==pos[i][1][1]){
				cond[i][1] = -1;
				if(pos[i][0][0]+1==pos[i][1][0]){
					cond[i][0] = -1;
					continue;
				}
				long s = 0;
				for(int j=pos[i][0][0]+1;j<pos[i][1][0];j++){
					char ch = map[j][pos[i][0][1]];
					if(ch=='.')continue;
					s |= 1<<((int)(ch-'A'));
				}
				cond[i][0] = s;
			}
			else{
				if(pos[i][0][1]<pos[i][1][1]){
					int ri = pos[i][0][0];
					int rj = pos[i][1][1];
					int li = pos[i][1][0];
					int lj = pos[i][0][1];
					long s = 0;
					for(int j=lj+1;j<=rj;j++){
						char ch = map[ri][j];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					for(int j=ri;j<li;j++){
						char ch = map[j][rj];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					cond[i][0] = s;
					s = 0;
					for(int j=ri+1;j<=li;j++){
						char ch = map[j][lj];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					for(int j=lj+1;j<rj;j++){
						char ch = map[li][j];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					cond[i][1] = s;
				}
				else{
					int li = pos[i][1][0];
					int lj = pos[i][1][1];
					int ri = pos[i][0][0];
					int rj = pos[i][0][1];
					long s = 0;
					for(int j=lj;j<rj;j++){
						char ch = map[ri][j];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					for(int j=ri;j<li;j++){
						char ch = map[j][lj];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					cond[i][0] = s;
					s = 0;
					for(int j=ri+1;j<=li;j++){
						char ch = map[j][rj];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					for(int j=lj+1;j<=rj;j++){
						char ch = map[li][j];
						if(ch=='.')continue;
						s |= 1<<((int)(ch-'A'));
					}
					cond[i][1] = s;
				}
			}
		}
		int c = 0;
		boolean update = true;
		long s = 0;
		while(update){
			update = false;
			for(int i=0;i<26;i++){
				if(!exist[i])continue;
				if(cond[i][0]!=-1&&((s&cond[i][0])==cond[i][0]) || cond[i][1]!=-1&&((s&cond[i][1])==cond[i][1])){
					exist[i] = false;
					c++;
					s|=1<<i;
					update = true;
				}
			}
		}
		System.out.println(c*2);
	}

	public static void main(String[] args) {
		new Main().run();
	}
}

import java.io.*;
import java.math.*;
import java.util.*;


class Main {
	static int[] vy = {1,0,-1,0};
	static int[] vx = {0,1,0,-1};
	static long mod = 1000000007;
	public static void main(String[] args) {	
		FastScanner sc = new FastScanner();
		//Scanner sc = new Scanner(System.in);
		PrintWriter out = new PrintWriter(System.out);
		int M = sc.nextInt();
		int N = sc.nextInt();
		char[][] map = new char[M][];
		for(int i = 0; i < M; i++) {
			map[i] = sc.next().toCharArray();
		}


		int sum = 0;
		boolean ok = true;
		while(ok) {
			ok = false;
			for(int i = 0; i < M; i++) {
				for(int j = 0; j < N; j++) {
					if(map[i][j] != '.') continue;
					int[] count = new int[26];
					for(int k = 0; k < 4; k++) {
						for(int l = 0; ; l++) {
							int tx = j + vx[k] * l;
							int ty = i + vy[k] * l;
							if(tx < 0 || ty < 0 || tx >= N || ty >= M) break;
							if(map[ty][tx] == '.') continue;
							count[map[ty][tx] - 'A']++;
							break;
						}
					}
					for(int kk = 0; kk < 26; kk++) {
						if(count[kk] == 2) {
							for(int k = 0; k < 4; k++) {
								for(int l = 0; ; l++) {
									int tx = j + vx[k] * l;
									int ty = i + vy[k] * l;
									if(tx < 0 || ty < 0 || tx >= N || ty >= M) break;
									if(map[ty][tx] == '.') continue;
									if(map[ty][tx] - 'A' != kk) break;
									map[ty][tx] = '.';
								} 
							}
							sum += 2;
							ok = true;
						}
					}
				}
			}

		}
		System.out.println(sum);
	}

	static class FastScanner {
	    private final InputStream in = System.in;
	    private final byte[] buffer = new byte[1024];
	    private int ptr = 0;
	    private int buflen = 0;
	    private boolean hasNextByte() {
	        if (ptr < buflen) {
	            return true;
	        }else{
	            ptr = 0;
	            try {
	                buflen = in.read(buffer);
	            } catch (IOException e) {
	                e.printStackTrace();
	            }
	            if (buflen <= 0) {
	                return false;
	            }
	        }
	        return true;
	    }
	    private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
	    private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
	    private void skipUnprintable() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;}
	    public boolean hasNext() { skipUnprintable(); return hasNextByte();}
	    public String next() {
	        if (!hasNext()) throw new NoSuchElementException();
	        StringBuilder sb = new StringBuilder();
	        int b = readByte();
	        while(isPrintableChar(b)) {
	            sb.appendCodePoint(b);
	            b = readByte();
	        }
	        return sb.toString();
	    }
	    public long nextLong() {
	        if (!hasNext()) throw new NoSuchElementException();
	        long n = 0;
	        boolean minus = false;
	        int b = readByte();
	        if (b == '-') {
	            minus = true;
	            b = readByte();
	        }
	        if (b < '0' || '9' < b) {
	            throw new NumberFormatException();
	        }
	        while(true){
	            if ('0' <= b && b <= '9') {
	                n *= 10;
	                n += b - '0';
	            }else if(b == -1 || !isPrintableChar(b)){
	                return minus ? -n : n;
	            }else{
	                throw new NumberFormatException();
	            }
	            b = readByte();
	        }
	    }
	    public int nextInt() {
	        if (!hasNext()) throw new NoSuchElementException();
	        int n = 0;
	        boolean minus = false;
	        int b = readByte();
	        if (b == '-') {
	            minus = true;
	            b = readByte();
	        }
	        if (b < '0' || '9' < b) {
	            throw new NumberFormatException();
	        }
	        while(true){
	            if ('0' <= b && b <= '9') {
	                n *= 10;
	                n += b - '0';
	            }else if(b == -1 || !isPrintableChar(b)){
	                return minus ? -n : n;
	            }else{
	                throw new NumberFormatException();
	            }
	            b = readByte();
	        }
	    }
	}
}

import java.util.*;
import java.lang.*;
import java.math.*;

public class Main {
	Scanner sc = new Scanner(System.in);

	void run() {
		int h = sc.nextInt();
		int w = sc.nextInt();

		char map[][] = new char[h][];

		for (int i = 0; i < h; i++) {
			map[i] = sc.next().toCharArray();
		}

		int mask[][] = new int[h][w];
		int max = 0;

		int y[] = new int[h];
		int t[] = new int[w];

		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if (map[i][j] == '.') {
					mask[i][j] = 0;
				} else {
					mask[i][j] = 1 << (map[i][j] - 'A');
					max = Math.max(map[i][j] - 'A', max);
				}
				y[i] |= mask[i][j];
				t[j] |= mask[i][j];
			}
		}
		max += 1;
		boolean dp[] = new boolean[1 << max];
		dp[0] = true;
		int n = max;
		int mk = 0;

		int nnt[] = new int[n];
		int nnt2[] = new int[n];
		int nny[] = new int[n];
		int nny2[] = new int[n];

		int memo[] = new int[n];
		Arrays.fill(memo, -1);
		int memo2[] = new int[n];
		Arrays.fill(memo, -1);

		for (int j = 0; j < n; j++) {
			int shj = 1 << j;

			int nt = -1;
			int nt2 = -1;
			int ny = -1;
			int ny2 = -1;
			for (int ii = 0; ii < h; ii++) {
				if ((y[ii] & shj) != 0) {
					if (nt == -1) {
						nt = ii;
					} else {
						nt2 = ii;
					}
				}
			}
			for (int ii = 0; ii < w; ii++) {
				if ((t[ii] & shj) != 0) {
					if (ny == -1) {
						ny = ii;
					} else {
						ny2 = ii;
					}
				}
			}
			if (nt2 == -1) {
				nt2 = nt;
			}
			if (ny2 == -1) {
				ny2 = ny;
			}

			if ((nt & ny) == -1) {
				nnt[j] = nt;
				nnt2[j] = nt2;
				nny[j] = ny;
				nny2[j] = ny2;
				continue;
			}

			if (mask[nt][ny] == shj) {
				int temp = ny2;
				ny2 = ny;
				ny = temp;
			}

			nnt[j] = nt;
			nnt2[j] = nt2;
			nny[j] = ny;
			nny2[j] = ny2;

			int tf = 0;
			int tf2 = 0;
			for (int ii = Math.min(nt, nt2); ii <= Math.max(nt, nt2); ii++) {
				tf |= mask[ii][ny];
				tf2 |= mask[ii][ny2];
			}

			int yf = 0;
			int yf2 = 0;
			for (int ii = Math.min(ny, ny2); ii <= Math.max(ny, ny2); ii++) {
				yf |= mask[nt][ii];
				yf2 |= mask[nt2][ii];

			}

			memo[j] = tf | yf;
			memo2[j] = tf2 | yf2;

		}
		/*
		 * System.out.println(nnt['Z'-'A']+" "+nnt2['Z'-'A']);
		 * System.out.println(nny['Z'-'A']+" "+nny2['Z'-'A']);
		 * System.out.println("-----------------");
		 * System.out.println(nnt['U'-'A']+" "+nnt2['U'-'A']);
		 * System.out.println(nny['U'-'A']+" "+nny2['U'-'A']);
		 * System.out.println("-----------------");
		 */

		for (int i = 0; i < 1 << n; i++) {
			if (!dp[i]) {
				continue;
			}
			for (int j = 0; j < n; j++) {
				int shj = 1 << j;
				if(dp[i|shj]){
//					System.out.println("here");
					continue;
				}

				int nt = nnt[j];
				int nt2 = nnt2[j];
				int ny = nny[j];
				int ny2 = nny2[j];

				if ((nt & ny) == -1) {
					continue;
				}
				if (Math.abs(nt - nt2) + Math.abs(ny2 - ny) < 2) {
					continue;
				}

				boolean ok = true;

				if (((~(i | shj)) & (memo[j])) == 0
						|| ((~(i | shj)) & (memo2[j])) == 0) {
					ok = true;
				} else {
					ok = false;
				}

				if (ok) {
//					 System.out.println(i + " " + Integer.bitCount(i) + " " +
//					 (char) (j + 'A'));
					 dp[i|shj]=true;
					 mk = Math.max(Integer.bitCount(i|shj), mk);
				}
			}
		}
		System.out.println(mk * 2);
	}

	public static void main(String[] args) {
		Main m = new Main();
		m.run();
	}
}

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {

		new CopyOfMain().run();
		return;
	}

	Scanner scanner = new Scanner(System.in);
	int m;
	int[][][] tile = new int[26][2][2];
	boolean[] cantGet =new boolean[26];
	int score = 0;

	void run() {

		m = scanner.nextInt();
		scanner.nextInt();

		for (int color = 0; color < 26; color ++) {

			tile[color][0][0] = -1;
			cantGet[color] = true;
		}

		for (int y = 0; y < m; y ++) {

			char[] line = scanner.next().toCharArray();

			for (int x = 0; x < line.length; x ++) {

				if(line[x] != '.') {

					int color = line[x] - 'A';
					cantGet[color] = false;
					int index = (tile[color][0][0] == -1) ? 0: 1;
					tile[color][index][0] = x;
					tile[color][index][1] = y;
				}
			}
		}

		int preScore;
		do {

			preScore = score;

			for (int color = 0; color < 26; color ++) {

				tryGet(color);
			}

		} while(preScore != score);

		System.out.println(score * 2);
	}

	void tryGet(int color) {

		if (cantGet[color]) {

			return;
		}

		if ((tile[color][0][0] == tile[color][1][0]
				&& Math.abs(tile[color][0][1] - tile[color][1][1]) == 1)
				|| (tile[color][0][1] == tile[color][1][1]
				&& Math.abs(tile[color][0][0] - tile[color][1][0]) == 1)) {

			cantGet[color] = true;
			return;
		}

		if((isEmpty(color, 1, tile[color][0][0], tile[color][0][1], tile[color][1][1])
				&& isEmpty(color, 0, tile[color][1][1], tile[color][0][0], tile[color][1][0]))
				|| (isEmpty(color, 1, tile[color][1][0], tile[color][0][1], tile[color][1][1])
						&& isEmpty(color, 0, tile[color][0][1], tile[color][0][0], tile[color][1][0]))) {

			get(color);
		}
	}

	boolean isEmpty(int skip,int axis, int point, int start, int end) {


		if (start == end) {

			return true;
		}

		if (start > end) {

			int swap = start;
			start = end;
			end = swap;
		}

		for (int color = 0; color < 26; color ++) {

			if (color == skip
					|| tile[color][0][0] == -1) {

				continue;
			}

			for (int index = 0; index < 2; index ++) {

				if (tile[color][index][1 - axis] == point
						&& tile[color][index][axis] >= start
						&& tile[color][index][axis] <= end) {

					return false;
				}
			}
		}

		return true;
	}

	void get(int color) {

		tile[color][0][0] = -1;
		score ++;
	}
}

