import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class Main {

	public static final int MAX = 500;

	public static boolean is_ok(int x, int y, int w, int h) {
		if (x < 0 || x >= w || y < 0 || y >= h) {
			return false;
		} else {
			return true;
		}
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);

		boolean[] places = new boolean[26];
		int[][] x_pos = new int[26][2];
		int[][] y_pos = new int[26][2];

		final int M = sc.nextInt();
		final int N = sc.nextInt();

		boolean[][] is_placed = new boolean[M][N];

		Arrays.fill(places, false);

		for (int i = 0; i < M; i++) {
			char[] input = sc.next().toCharArray();

			for (int j = 0; j < N; j++) {
				if ('A' <= input[j] && input[j] <= 'Z') {
					is_placed[i][j] = true;

					int count = places[input[j] - 'A'] ? 1 : 0;
					places[input[j] - 'A'] = true;
					x_pos[input[j] - 'A'][count] = j;
					y_pos[input[j] - 'A'][count] = i;
				} else {
					is_placed[i][j] = false;
				}
			}
		}

		int score = 0;
		while (true) {
			boolean updated = false;

			for (int mozi = 0; mozi < 26; mozi++) {
				if (!places[mozi]) {
					continue;
				}

				int fst_min_x = x_pos[mozi][0];
				int fst_max_x = x_pos[mozi][0];
				int fst_min_y = y_pos[mozi][0];
				int fst_max_y = y_pos[mozi][0];
				int snd_min_x = x_pos[mozi][1];
				int snd_max_x = x_pos[mozi][1];
				int snd_min_y = y_pos[mozi][1];
				int snd_max_y = y_pos[mozi][1];

				for (int x = x_pos[mozi][0] - 1;; x--) {
					if (!is_ok(x, y_pos[mozi][0], N, M)) {
						break;
					} else if (is_placed[y_pos[mozi][0]][x]) {
						break;
					}

					fst_min_x = x;
				}
				for (int x = x_pos[mozi][0] + 1;; x++) {
					if (!is_ok(x, y_pos[mozi][0], N, M)) {
						break;
					} else if (is_placed[y_pos[mozi][0]][x]) {
						break;
					}

					fst_max_x = x;
				}
				for (int y = y_pos[mozi][0] - 1;; y--) {
					if (!is_ok(x_pos[mozi][0], y, N, M)) {
						break;
					} else if (is_placed[y][x_pos[mozi][0]]) {
						break;
					}

					fst_min_y = y;
				}
				for (int y = y_pos[mozi][0] + 1;; y++) {
					if (!is_ok(x_pos[mozi][0], y, N, M)) {
						break;
					} else if (is_placed[y][x_pos[mozi][0]]) {
						break;
					}

					fst_max_y = y;
				}

				for (int x = x_pos[mozi][1] - 1;; x--) {
					if (!is_ok(x, y_pos[mozi][1], N, M)) {
						break;
					} else if (is_placed[y_pos[mozi][1]][x]) {
						break;
					}

					snd_min_x = x;
				}
				for (int x = x_pos[mozi][1] + 1;; x++) {
					if (!is_ok(x, y_pos[mozi][1], N, M)) {
						break;
					} else if (is_placed[y_pos[mozi][1]][x]) {
						break;
					}

					snd_max_x = x;
				}
				for (int y = y_pos[mozi][1] - 1;; y--) {
					if (!is_ok(x_pos[mozi][1], y, N, M)) {
						break;
					} else if (is_placed[y][x_pos[mozi][1]]) {
						break;
					}

					snd_min_y = y;
				}
				for (int y = y_pos[mozi][1] + 1;; y++) {
					if (!is_ok(x_pos[mozi][1], y, N, M)) {
						break;
					} else if (is_placed[y][x_pos[mozi][1]]) {
						break;
					}

					snd_max_y = y;
				}

				//System.out.println((char)(mozi + 'A') + "  " + x_pos[mozi][0] + " " + y_pos[mozi][0] + " " + x_pos[mozi][1] + " " + y_pos[mozi][1]);
				//System.out.println((char)(mozi + 'A') + "1 " + fst_min_x + " " + fst_max_x + " "+ fst_min_y + " " + fst_max_y);
				//System.out.println((char)(mozi + 'A') + "2 " + snd_min_x + " " + snd_max_x + " "+ snd_min_y + " " + snd_max_y);

				boolean flag = false;
				flag |= (fst_max_y >= y_pos[mozi][1]
						&& x_pos[mozi][0] <= x_pos[mozi][1] && x_pos[mozi][0] >= snd_min_x);
				flag |= (fst_max_y >= y_pos[mozi][1]
						&& x_pos[mozi][0] >= x_pos[mozi][1] && x_pos[mozi][0] <= snd_max_x);

				flag |= (snd_min_y <= y_pos[mozi][0]
						&& x_pos[mozi][0] <= x_pos[mozi][1] && x_pos[mozi][1] <= fst_max_x);
				flag |= (snd_min_y <= y_pos[mozi][0]
						&& x_pos[mozi][0] >= x_pos[mozi][1] && x_pos[mozi][1] >= fst_min_x);
				flag |= (x_pos[mozi][0] == x_pos[mozi][1] && fst_max_y >= snd_min_y);
				flag |= (y_pos[mozi][0] == y_pos[mozi][1] && x_pos[mozi][0] <= x_pos[mozi][1] && fst_max_x >= snd_min_x);
				flag |= (y_pos[mozi][0] == y_pos[mozi][1] && x_pos[mozi][0] >= x_pos[mozi][1] && fst_min_x <= snd_max_x);

				if (flag) {
					score += 2;
					is_placed[y_pos[mozi][0]][x_pos[mozi][0]] = false;
					is_placed[y_pos[mozi][1]][x_pos[mozi][1]] = false;

					places[mozi] = false;

					updated = true;
					//System.out.println("erase " + (char)(mozi + 'A'));
				}
			}

			if (!updated) {
				break;
			}
		}

		System.out.println(score);

	}

}

import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Collection;
import java.util.Deque;
import java.util.Scanner;

public class Main {

	public static void main(String[] args) {

		new Main().run();
	}

	Scanner scanner = new Scanner(System.in);
	int m;
	int[][][] tile = new int[26][2][2];
	Deque<Integer> que = new ArrayDeque<Integer>();
	boolean[] cantGet =new boolean[26];
	boolean[] check =new boolean[26];
	int score = 0;

	void run() {

		m = scanner.nextInt();
		scanner.nextInt();

		for (int color = 0; color < tile.length; color ++) {

			tile[color][0][0] = -1;
			cantGet[color] = false;
			check[color] = false;
		}

		for (int y = 0; y < m; y ++) {

			char[] line = scanner.next().toCharArray();

			for (int x = 0; x < line.length; x ++) {

				if(line[x] != '.') {

					int color = line[x] - 'A';
					int index = (tile[color][0][0] == -1) ? 0: 1;
					tile[color][index][0] = x;
					tile[color][index][1] = y;
				}
			}
		}

		for (int color = 0; color < tile.length; color ++) {

			if (tile[color][0][0] != -1
					&& ! tryGet(color) && ! que.isEmpty()) {

				bfs();
			}
		}

		System.out.println(score * 2);
	}

	Collection<Integer> old = new ArrayList<Integer>(que);

	void bfs() {

		do {

			old.clear();
			old.addAll(que);
			int color;
			int last = que.getLast();
			do {

				color = que.remove();
				tryGet(color);

			} while (color != last);

		} while (! que.isEmpty() && ! old.containsAll(que));
	}

	boolean tryGet(int color) {

		if (cantGet[color]) {

			return false;
		}

		if (! check[color]) {

			check[color] = true;
			if ((tile[color][0][0] == tile[color][1][0]
					&& Math.abs(tile[color][0][1] - tile[color][1][1]) == 1)
					|| (tile[color][0][1] == tile[color][1][1]
					&& Math.abs(tile[color][0][0] - tile[color][1][0]) == 1)) {

				cantGet[color] = true;
				return false;
			}
		}

		if((isEmpty(color, 1, tile[color][0][0], tile[color][0][1], tile[color][1][1])
					&& isEmpty(color, 0, tile[color][1][1], tile[color][0][0], tile[color][1][0]))
				|| (isEmpty(color, 1, tile[color][1][0], tile[color][0][1], tile[color][1][1])
					&& isEmpty(color, 0, tile[color][0][1], tile[color][0][0], tile[color][1][0]))) {

			tile[color][0][0] = -1;
			score ++;
			return true;
		}

		que.add(color);
		return false;
	}

	boolean isEmpty(int skip,int axis, int point, int start, int end) {

		if (start == end) {

			return true;
		}

		if (start > end) {

			int swap = start;
			start = end;
			end = swap;
		}

		boolean isEmpty = true;
		for (int color = 0; color < tile.length; color ++) {

			if (color == skip
					|| tile[color][0][0] == -1) {

				continue;
			}

			for (int index = 0; index < tile[0].length; index ++) {

				if (tile[color][index][1 - axis] == point
						&& tile[color][index][axis] >= start
						&& tile[color][index][axis] <= end) {

					isEmpty = false;
					if (! que.contains(color)
							&& ! cantGet[color]) {

						que.add(color);
					}
					break;
				}
			}
		}

		return isEmpty;
	}
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {

	static Scanner sc = new Scanner(System.in);
	static char[][] f;;

	public static void main(String[] arg) {
		int M = sc.nextInt();
		int N = sc.nextInt();
		f = new char[M][N];
		for (int i = 0; i < M; ++i) {
			f[i] = sc.next().toCharArray();
		}
		int[][] r = new int[26][2];
		int[][] c = new int[26][2];
		for (int i = 0; i < 26; ++i) {
			r[i][0] = r[i][1] = c[i][0] = c[i][1] = -1;
		}
		boolean[] used = new boolean[26];
		Arrays.fill(used, true);
		for (int i = 0; i < M; ++i) {
			for (int j = 0; j < N; ++j) {
				if (f[i][j] == '.') continue;
				int ch = f[i][j] - 'A';
				used[ch] = false;
				if (r[ch][0] >= 0) {
					r[ch][1] = i;
					c[ch][1] = j;
				} else {
					r[ch][0] = i;
					c[ch][0] = j;
				}
			}
		}
		int ans = 0;
		for (int i = 0; i < 26; ++i) {
			for (int j = 0; j < 26; ++j) {
				if (used[j]) continue;
				if (remove(r[j][0], c[j][0], r[j][1], c[j][1])) {
					ans += 2;
					used[j] = true;
					f[r[j][0]][c[j][0]] = '.';
					f[r[j][1]][c[j][1]] = '.';
				}
			}
		}
		System.out.println(ans);
	}

	static boolean remove(int r1, int c1, int r2, int c2) {
		if (r1 == r2) {
			if (Math.abs(c1 - c2) <= 1) return false;
			for (int i = Math.min(c1, c2) + 1; i < Math.max(c1, c2); ++i) {
				if (f[r1][i] != '.') return false;
			}
			return true;
		} else if (c1 == c2) {
			if (Math.abs(r1 - r2) <= 1) return false;
			for (int i = Math.min(r1, r2) + 1; i < Math.max(r1, r2); ++i) {
				if (f[i][c1] != '.') return false;
			}
			return true;
		} else {
			if (r1 > r2) {
				int tmp = r1;
				r1 = r2;
				r2 = tmp;
				tmp = c1;
				c1 = c2;
				c2 = tmp;
			}
			{
				boolean ok = true;
				for (int i = r1 + 1; i <= r2; ++i) {
					if (f[i][c1] != '.') {
						ok = false;
						break;
					}
				}
				int move = c1 < c2 ? 1 : -1;
				for (int i = c1; i != c2; i += move) {
					if (f[r2][i] != '.') {
						ok = false;
						break;
					}
				}
				if (ok) return true;
			}
			{
				boolean ok = true;
				for (int i = r2 - 1; i >= r1; --i) {
					if (f[i][c2] != '.') {
						ok = false;
						break;
					}
				}
				int move = c1 < c2 ? -1 : 1;
				for (int i = c2; i != c1; i += move) {
					if (f[r1][i] != '.') {
						ok = false;
						break;
					}
				}
				if (ok) return true;
			}
			return false;
		}
	}
}

import java.util.*;

public class Main{
	char [][] data;
	int w,h;
	int [] vx = {0,1,0,-1};
	int [] vy = {1,0,-1,0};

	private void doit(){
		Scanner sc = new Scanner(System.in);
		while(sc.hasNext()){
			h = sc.nextInt();
			w = sc.nextInt();
			data = new char[h][w];
			for(int i = 0; i < h; i++){
				data[i] = sc.next().toCharArray();
			}
			int sum = 0;
			for(int k = 0; k < 26; k++){
				int prev = sum;
				for(int i = 0; i < h; i++){
					for(int j = 0; j < w; j++){
						if(data[i][j] == '.'){
							sum += erase(i,j);
						}
					}
				}
				if(prev == sum){
					break;
				}
			}

			System.out.println(sum);
		}
	}

	private int erase(int y, int x) {
		char [] panel = new char[4];
		Arrays.fill(panel, '*');
		int [][] pos = new int[4][2];
		for(int i = 0; i < 4; i++){
			int xx = x + vx[i];
			int yy = y + vy[i];
			while(true){
				if(! isOK(xx,yy)) break;
				if(data[yy][xx] != '.'){
					panel[i] = data[yy][xx];
					pos[i][0] = xx;
					pos[i][1] = yy;
					break;
				}
				xx = xx + vx[i];
				yy = yy + vy[i];
			}
		}
		int count = 0;
		for(int i = 0; i < 4; i++){
			if(panel[i] == '*') continue;
			for(int j = i + 1; j < 4; j++){
				if(i == j) continue;

				if(panel[i] == panel[j]){
					data[pos[i][1]][pos[i][0]] = '.';
					data[pos[j][1]][pos[j][0]] = '.';
					count+= 2;
				}
			}
		}
		return count;
	}

	private boolean isOK(int xx, int yy) {
		if(0 <= xx && xx < w && 0 <= yy && yy < h){
			return true;
		}
		return false;
	}

	private void debug(Object... o) {
		System.out.println("debug = " + Arrays.deepToString(o));
	}

	public static void main(String[] args) {
		new Main().doit();
	}
}

