using namespace std;

#define INF 999999999

int N, M, L;
double P[102], T[102], V[102];
double dp[102][102];
double p[102][102];

int main(){
  while(cin >> N >> M >> L){
    for(int i = 0; i < N; i++){
      cin >> P[i] >> T[i] >> V[i];
      P[i] /= 100.0;
    }

    for(int k = 0; k < N; k++){
      memset(dp, 0, sizeof(dp));
      dp[0][0] = 1.0;

      for(int i = 0; i < M; i++){
        for(int j = 0; j <= M; j++){
          dp[i + 1][j] += dp[i][j] * (1 - P[k]);
          dp[i + 1][j + 1] += dp[i][j] * P[k];
        }
      }

      for(int j = 0; j <= M; j++){
        p[k][j] = dp[M][j];
      }
    }

    for(int i = 0; i < N; i++){
      double ans = 0;

      for(int j = 0; j <= M; j++){
        double ti = INF;
        if(V[i] != 0) ti = (double)j * T[i] + (double)L / V[i];

        double mul = 1.0;

        for(int k = 0; k < N; k++){
          if(k == i) continue;

          double sum = 0;

          for(int l = 0; l <= M; l++){
            double tk = INF;
            if(V[k] != 0) tk = (double)l * T[k] + (double)L / V[k];
            if(ti >= tk) continue;
            sum += p[k][l];
          }

          mul *= sum;
        }

        ans += mul * p[i][j];
      }

      printf("%.8lf\n", ans);
    }
  }
}

