using namespace std;

int main(){
	int n;
	int m;
	int l;
	cin >> n >> m >> l;
	vector<int> p;
	vector<int> t;
	vector<int> v;
	int a;
	for ( int i = 0; i < n; i++ ) {
		cin >> a;
		p.push_back(a);
		cin >> a;
		t.push_back(a);
		cin >> a;
		v.push_back(a);
	}
	vector<int> c;
	for ( int i = 0; i <= m; i++ ) {
		a = 1;
		for ( int j = m - i + 1; j <= m; j++ ) {
			a = a * j;
		}
		for ( int j = 1; j <= i; j++ ) {
			a = a / j;
		}
		c.push_back(a);
	}
	double goaltime;
	double pro;
	double s;
	for ( int i = 0; i < n; i++ ) {
		s = 0.00;
		if ( v[i] == 0 ) {
			cout << fixed << s << endl;
			continue;
		}
		for ( int x = 0; x <= m; x++ ) {
			goaltime = double(l) / v[i] + t[i] * x;
			pro = pow( 0.01*p[i] , x ) * pow( 1.00 - 0.01*p[i] , m - x ) * c[x];
			for ( int j = 0; j < n; j++ ) {
				if ( i == j ) continue;
				if ( v[j] == 0 ) {
					cout << fixed << 0.00 << endl;
					continue;
				}
				double g;
				g = goaltime - double(l) / v[j];
				int y;
				if ( t[j] == 0 ) {
					if ( g >= 0 ) y = m + 1;
				}else {
					y = (int)g / t[j] + 1;
				}
				if ( g < 0 ) y = 0;
				if ( y > m ) {
					pro = 0.00;
					break;
				}
				double z = 0.00;
				for ( y; y <= m; y++ ) {
					z += pow( 0.01*p[j] , y ) * pow( 1.00 - 0.01*p[j] , m - y ) * c[y];
				}
				pro = pro * z;
			}
			s += pro;
		}
		cout << fixed << s << endl;
	}
	return 0;
}

