        vector<vector<ld>> comb(m+1,vector<ld>(m+1,0));
        comb[0][0]=1;
        for(int j=0;j<=m;j++){
            for(int k=0;k<=j;k++){
                if(k==0){
                    comb[j][k]=(p[i]==0 ? 0 : pow(1-p[i],j));
                }
                else if(k==j){
                    comb[j][k]=(p[i]==0 ? 0 : pow(p[i],j));
                }
                else{
                    comb[j][k]=comb[j-1][k-1]*p[i]+comb[j-1][k]*(1-p[i]);
                }
            }
        }
        pos[i]=comb[m];
    }
    vector<vector<ld>> possum(n,vector<ld>(m+2));
    for(int i=0;i<n;i++){
        partial_sum(pos[i].begin(),pos[i].end(),possum[i].begin()+1);
    }

    vector<ld> res(n);
    for(int i=0;i<n;i++){
        for(int x=0;x<=m;x++){
            ld sc=pos[i][x];
            for(int j=0;j<n;j++){
                if(i==j) continue;
                if(t[j]==0){
                    if(l*v[j]+x*t[i]*v[i]*v[j]>=l*v[i]){
                        sc=0;
                    }
                    continue;
                }
                ld d=((ld(l)/v[i]-ld(l)/v[j])+x*t[i])/t[j]+1e-9;
                int y=ceil(d);
                y=min(max(y,0),m+1);
                sc*=possum[j][m+1]-possum[j][y];                
            }
            res[i]+=sc;
        }
    }
    cout<<setprecision(10)<<fixed;
    for(int i=0;i<n;i++){
        cout<<res[i]<<endl;
    }

    return 0;
}

