			int cnt1 = i, cnt2 = j;
			for(int k = 1; k <= i; k++){
				cb[u][i] = cb[u][i]*(m-k+1)/k;
				while(cb[u][i] > 1.0){
					if(cnt1 > 0){
						cb[u][i] *= p[u];
						cnt1--;
					} else if(cnt2 > 0){
						cb[u][i] *= (1-p[u]);
						cnt2--;
					}
					if(cnt1 == 0 && cnt2 == 0) break;
				}
			}
			rep(k,cnt1) cb[u][i] *= p[u];
			rep(k,cnt2) cb[u][i] *= (1-p[u]);
		}
	}
}

