inline void calC()
{
	int i,j;C[0][0]=1;C[1][0]=1;C[1][1]=1;
	for(i=2;i<=50;i++)
	{
		C[i][i]=1;C[i][0]=1;
		for(j=1;j<i;j++)
			C[i][j]=C[i-1][j]+C[i-1][j-1];
	}
}
inline void calR()
{
	int i,j;
	for(i=1;i<=N;i++)
	{
		rest[i][0]=L*1.0/(V[i]*1.0);
		for(j=1;j<=M;j++)
			rest[i][j]=rest[i][j-1]+T[i];
	}
}
inline void calP()
{
	int i,j;
	for(i=1;i<=N;i++)
		for(j=0;j<=M;j++)
			P[i][j]=C[M][j]*pow(X[i],j*1.0)*pow((1-X[i]),(M-j)*1.0);
}
inline double cal(int pos,double MAXN)
{
	int i,j;double temp;
	double ret=1.0;
	for(i=1;i<=N;i++)
	{
		if(i==pos)
			continue;
		temp=0.0;
		for(j=M;j>=0&&rest[i][j]>MAXN;j--)
			temp+=P[i][j];
		ret*=temp;
	}
	return ret;
}
inline void solve()
{
	int i,j;
	calC();calR();calP();
	for(i=1;i<=N;i++)
	{
		ans[i]=0.0;double temp=1.0;
		for(j=0;j<=M;j++)
			ans[i]+=P[i][j]*cal(i,rest[i][j]);
	}
	for(i=1;i<=N;i++)
		printf("%.7lf\n",ans[i]);
}
