            if (j > 0) times[i][j] = times[i][j-1] + T[i];
            prob[i][j] = pow(P[i], j) * pow(1.000 - P[i], M - j) * comb[M][j];
        }
    }

    for (int i = 0; i < N; i++) {
        double ans = 0.0000;
        for (int j = 0; j <= M; j++) {
            double tmp = 1.0000;
            for (int k = 0; k < N; k++) {
                if (k == i) continue;
                double sum = 0.0000;
                for (int l = 0; l <= M; l++) {
                    if (times[i][j] < times[k][l]) sum += prob[k][l];
                }
                tmp *= sum;
            }
            ans += prob[i][j] * tmp;
        }
        printf("%.10f\n", ans);
    }


