double zu[N][N];
int n;
struct node{
	double p,t,v;
}data[N];
void zuhe()
{
	memset(zu,0,sizeof(zu));
	int i,j;
	for(i=0;i<=50;i++)//i=0,wa
	{
		zu[i][0]=1;
		zu[i][1]=i;
	}
	for(i=1;i<=50;i++)
		for(j=1;j<=i;j++)
			zu[i][j]=zu[i-1][j]+zu[i-1][j-1];
}
int main()
{
	int i,j,z,k,m,l;
	double sum,res,time1,time2;
	zuhe();
	while(scanf("%d%d%d",&n,&m,&l)!=EOF)
	{
		for(i=1;i<=n;i++)
			scanf("%lf%lf%lf",&data[i].p,&data[i].t,&data[i].v);
		for(i=1;i<=n;i++)
			for(j=0;j<=m;j++)
				p[i][j]=zu[m][j]*pow(data[i].p/100.0,j*1.0)*pow((1-data[i].p/100.0),(m-j)*1.0);
		for(i=1;i<=n;i++)
		{
			ans[i]=0;
			for(j=0;j<=m;j++)
			{
				res=p[i][j];
				time1=j*data[i].t+l/data[i].v;
				for(z=1;z<=n;z++)
				{
					sum=0;
					if(z==i)
						continue;
					for(k=0;k<=m;k++)
					{
						time2=k*data[z].t+l/data[z].v;
						if(time1<time2)
							sum+=p[z][k];
					}
					res*=sum;
				}
				ans[i]+=res;
			}
		}
		for(i=1;i<=n;i++)
			printf("%.8lf\n",ans[i]);
	}
	return 0;
}



