using namespace std;
double pt[101][101];
int dat[101][3],n,m,l;

bool readin()
{
	if(scanf("%d%d%d",&n,&m,&l)!=3) return false;
	for(int i=1;i<=n;++i)
	 scanf("%d%d%d",&dat[i][0],&dat[i][1],&dat[i][2]);
    return true;
}

int com(int m,int n)    
{    
   if(m==1)    
     return n;    
   else    
     return com(m-1,n)*(n-m+1)/m;    
}   



double cal(int i,int j)
{
	double mid=0,m1,m2,p1=(double)dat[i][0]/100,p2=(double)dat[j][0]/100;
	double t1=(double)l/dat[i][2],t2=(double)l/dat[i][1];
	for(int k=0;k<=m;++k)
	{
		double nt1=t1+k*dat[i][1];
		int k1=min((int)floor((nt1-t2)/dat[j][1]),m);
		m1=(double)com(k,m)*pow(p1,k)*pow(1-p1,m-k);
		m2=(double)com(k1,m)*pow(p2,k1)*pow(1-p2,m-k1);
		mid+=m1*m2;
	}
	pt[i][j]=mid;
	return mid;
}

main()
{
	//freopen("in.txt","r",stdin);
	while(readin())
	{
		for(int i=1;i<=n-1;++i)
		 for(int j=i+1;j<=n;++j)
		  pt[j][i]=1-cal(i,j);
	    double mid=0;
	    for(int i=1;i<=n;++i)
	    {
	    	mid=1;
    		for(int j=1;j<=n;++j)
   		    {
    		   	if(i==j) continue;
    		   	mid*=pt[i][j];
 		    }
 		    printf("&lf\n",mid);
    	}
	}
}

