long double comb(int a, int b) {
	long double ans = 1.0L;
	for (int i = 0; i < b; ++i) {
		ans *= (long double)(a - i);
		ans /= (long double)(i + 1);
	}
	return ans;
}

long double prob(long double percent, int a, int b) {
	long double ans = 1.0L;
	percent /= 100.0L;
	for (int i = 0; i < a; ++i) {
		if (i < b) {
			ans *= percent;
		}
		else {
			ans *= (1.0L - percent);
		}
	}
	ans *= comb(a, b);
	return ans;
}

void solve(){
	int n, m, l;
