	}
	REP(i, n) {
		long double ans = 0;
		for (int q = 0; q <= m; ++q) {
			long double req_time = q * get<1>(inputs[i]);
			req_time += get<2>(inputs[i]);
			long double pre = prob(get<0>(inputs[i]), m, q);
			REP(j, n) {
				if (j == i) continue;
				long double tmp = 0.0L;
				for (int t = 0; t <= m; ++t) {
					long double now_time = t * get<1>(inputs[j]);
					now_time += get<2>(inputs[j]);
					if (now_time > req_time + eps) {
						tmp += prob(get<0>(inputs[j]), m, t);
					}
				}
				pre *= tmp;
			}
			ans += pre;
		}
		cout << ans << endl;
	}
}

