typedef vector<vd> vvd;

int fac(int a)
{
	int ret = 1;

	rep(i, 1, a + 1)
	{
		ret *= i;
	}

	return ret;
}




int aCb(int a, int b)
{
	if (a < b) return 0;
	return fac(a) / (fac(a - b) * fac(b));
}

int main()
{
	int N, M, L;
	cin >> N >> M >> L;
	vi P(N), T(N), V(N);
	rep(i, 0, N) cin >> P[i] >> T[i] >> V[i];

	vvd per(N, vd(M+1, 0));
	rep(i, 0, N)
	{
		double p = P[i] / 100.0;
		double pp = 1.0 - p;
		rep(j, 0, M + 1) per[i][j] = pow(pp, M - j) * pow(p, j) * (double)aCb(M, j);
	}

	vvd imos(N, vd(M + 2, 0));
	rep(i, 0, N)
	{
		rep(j, 0, M + 1) imos[i][j] = per[i][j];
		rrep(j, M, 0) imos[i][j] += imos[i][j + 1];
	}

	rep(i, 0, N)
	{
		double ans = 0;
		rep(j, 0, M + 1)
		{
			double _ans = per[i][j];
			double deadline = (double)L / V[i] + (double)T[i] * j;

			rep(k, 0, N)
			{
				if (i == k) continue;
				double fastest = (double)L / V[k];
				if (deadline < fastest - EPS) continue;

				if (T[k] == 0)
				{
					_ans = 0;
					continue;
				}

				int minrest = (deadline - fastest + T[k] - 1) / T[k];
				double test = fastest + T[k] * minrest;
				if (abs(deadline - test) < EPS) minrest++;
				if (M < minrest)
				{
					_ans = 0;
					continue;
				}
				_ans *= imos[k][minrest];
			}

			ans += _ans;
		}
		printf("%.10f\n", ans);
	}
}

